[[module css]]

#page-content .rate-box-with-credit-button {
    background: #5C1D24;
}
#page-content .rate-box-with-credit-button>.creditButton>p>a:before {
    background-color: #FFB961;
}
#header h2 span::before {
    content: "艱 難 を 経 て 星 へ";
    color: #ffc780;
    font-family: sans-serif;
    font-weight: 400;
    font-size: 100%;
    text-shadow: 3px 3px 3px rgba(0,0,0,.9);
}
.danger-diamond a:hover {
    background-color: transparent;
}
.hovertip {
    width: auto !important;
    max-width: 550px;
}
.f-footer{
    display: none;
}
.hover{
    text-decoration: none;
    border-bottom: 1px solid;
}
.hover:hover{
    text-decoration: none;
    border-bottom: none;
}
.hover .tip{    
    visibility: hidden;
    position: absolute;
    opacity: 0;
    transition: opacity 500ms;
}
.hover:hover .tip{
    visibility: visible;
    position: absolute;
    display: inline;
    opacity: 1;
    color: inherit;
    /* font-size: 80%; */
    margin: 25px -12px;
    height: auto;
    background: #1B304D;
    border: 1px solid #CCC;
    width: auto;
    padding: 1em;
    border-radius: 7px;
    box-shadow: 0 2px 4px rgba(100,100,100,0.3);
    z-index: 20;
}
.hover:hover .tip span{
    position: relative;
    margin: auto;
    height: auto;
    width: auto;
    border: none;
    padding: 0;
}
.footnotes-footer{display:none;}
    overflow-x: auto;
    /* white-space: nowrap; */
}
#page-content a:hover::before {
    content: "";
}
[[/module]]

