[[module css]]
.danke{
padding: 5px 5px 5px 2px;
margin-bottom:10px;
width: 100%;
}
.site{
background-color:#ffff7ff;
border: 3px solid #ffff7ff;
color: #17171a;
}
div.text{
    color: #17171a;
    padding-left: 1em;
    font-size: 12px;
    }
div.text a {
    color: #02c7b3;
    text-decoration: none;
    background: transparent;
    font-size: 12px;
}
div.text a:hover {
    color: #ffabd8;
    text-decoration: none;
    background: transparent;
}
[[/module]]
