@import url(http://scpsandboxcn.wikidot.com/local--files/tnsbilws/TING-Theme.css);

@media (max-width: 479px) {

#search-top-box,#side-bar,#login-status {
display:none
}
}

@media (max-width: 1700px){
.phone {
    font-size: 60%;
}
}

a:visited {
    color: #565656;
}

a {
    color: #404040;
    text-decoration: none;
