    }
}

/*INTERWIKI */
 #interwiki body{
     overflow: hidden;
}
 #interwiki .side-block{
     background: hsl(127, 50%, 94%);
     border-color: var(--dark);
     border-radius: 0;
     box-shadow: 0 2px 6px hsla(0, 0%, 40%, .5);
}
 #interwiki .heading{
     color: var(--dark);
     border-color: var(--sapphire);
}

/* SAPPHIRE FORMAT */
 .sapphire-block {
     border: 5px solid #0d0e26;
     background-color: #abafed;
     padding: 0 1em;
     margin: 1em 0;

     color: #222;
     font-family: 'Tinos', serif;
     font-size: 12.5pt;
     text-align: justify;
     word-break: unset;
}
 .sapphire-block hr {
     background-color: #0d0e26;
     border: none;
     margin: auto;
     height: 2px;
     width: 100%;
}
 .sapphire-lite-hr hr {
     background-color: #0d0e26;
     border: none;
     margin: auto;
     height: 2px;
     width: 45%;
}
 div.sapphire-excerpt {
     border: 1px solid #0d0e26;
     background-color: #dedfed;
     padding: 0 1em;
     margin: 1em 2em;
}
 .sapphire-heading-2,
 .sapphire-heading-3,
 .sapphire-heading-4 {
     color: #151a61;
     color: var(--sapphire-muted);
     font-weight: bold;
     word-break: unset;
     line-height: 120%;
}
 .sapphire-heading-2 { font-size: 150%; }
 .sapphire-heading-3 { font-size: 130%; }
 .sapphire-heading-4 { font-size: 120%; }

/* MOBILE VIEW */
 @media (max-width: 479px) {
     div.sapphire-excerpt {
         margin: 1em 0;
    }
}
 @media (min-width: 480px) and (max-width: 580px) {
     div.sapphire-excerpt {
         margin: 0.5em;
    }
}
[[/module]]
