    background-size: auto 120px;
    filter: drop-shadow(0px 5px 10px var(--color-3));
}
#header h1 a::before {
    content: "TOMATOMANIA";
    font-size: 30px;
    color: var(--color-1);
    text-align: center;
    letter-spacing: 0;
    font-family: 'Abril Fatface', cursive;
    text-shadow: /*.05em .05em 0 var(--color-1), -.05em .05em 0 var(--color-1), .05em -.05em 0 var(--color-1), -.05em -.05em 0 var(--color-1), .05em 0 0 var(--color-1), -.05em 0 0 var(--color-1), 0 .05em 0 var(--color-1), 0 -.05em 0 var(--color-1)*/ 0px 5px 10px var(--color-3);
    display: block;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    opacity: 1;
    z-index: 1;
}
/* トップメニュー */
#top-bar {
    width: 100%;
    position: absolute;
    z-index: 50;
    top: 140px;
    height: 21px;
    line-height: 18px;
    padding: 0;
    z-index: 20;
    font-size: 90%;
}
#top-bar ul {
    float: left;
    display: flex;
    width: 100%;
}
#top-bar li {
    flex: 1;
    margin: 0;
}
#top-bar a {
    color: var(--color-1);
    background: transparent;
    border-radius: 5px;
    transition: all .2s ease-in-out;
}
#top-bar ul li {
    border: 0;
    position: relative;
}
#top-bar ul li ul {
    display: block;
    border: solid 1px var(--color-2);
    background: var(--color-2);
    box-shadow: 0 3px 3px var(--color-3);
    border-top: 0;
    border-radius: 5px;
    transition: all .2s ease-in-out;
}
#top-bar ul li a {
    border-left: none;
    border-right: none;
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
