div#container-wrap {
    background: url(http://dog-punch.wikidot.com/local--files/memo:image/00) repeat-x;
}

.p-paper {
    background: #fbfbfb;
    box-shadow: 0 0 10px rgba(0,0,0,0.3);
    margin: 26px auto 0;
    max-width: 900px;
    min-height: 300px;
    padding: 24px;
    position: relative;
    width: 80%;
    line-height: 200%;
}
.p-paper:before {
    background: #fafafa;
    box-shadow: 0 0 8px rgba(0,0,0,0.2);
    left: -5px;
    top: 4px;
    transform: rotate(-2.5deg);
}
.p-paper:after {
    background: #f6f6f6;
    box-shadow: 0 0 3px rgba(0,0,0,0.2);
    right: -3px;
    top: 1px;
    transform: rotate(1.4deg);
}
.p-paper:before, .p-paper:after {
    content: "";
    height: 98%;
    position: absolute;
    width: 100%;
    z-index: -1;
}

[[/module]]

