div#container-wrap {
    background: url(http://scp-jp.wikidot.com/local--files/tono-logo/haikei.png) top left repeat-x;
}
div#header {
    background: url(http://scp-jp.wikidot.com/local--files/tono-logo/tono_green.png) 25px 55px no-repeat;
    position: relative;
    background-size: 68px 68px;
}
@media (max-width: 580px) {
  div#header {
    background-position: 12px 55px;
  }
}
@media (max-width: 479px) {
  div#header {
    background-position: 0px 55px;
  }
}
div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "遠野妖怪保護區";
    color: #4d4d4d;
    text-shadow: 0px 0px 1px;
font-family: MS ゴシック, Microsoft JhengHei, PMingLiU;
}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: "官方網站";
    font-weight: bold;
    margin: 0 1px;
    color: #4d4d4d;
    padding: 19px 0;
    text-shadow: 0px 0px 1px;
    white-space: pre;
font-family: MS ゴシック, PMingLiU, Microsoft JhengHei;
}

[[/module]]

[[module CSS]]

.page-rate-widget-box .rate-points{
    background-color: #666 !important;
    color: #FFF !important;
    font-weight: bold !important;
    padding: 0 10px !important;
    -moz-border-radius: 4px 0 0 4px !important;
    border-radius: 0 !important;
        border: none !important;
}

.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
    border: none !important;
}

.page-rate-widget-box .cancel a:hover {
    border-radius:0 !important;
}

.page-rate-widget-box a{
    text-decoration: none !important;
    color: #000 !important;
    background-color: #DDD !important;
    padding: 0 5px !important;
}
.page-rate-widget-box a:hover{
    background-color: #EEE !important;
}

.page-rate-widget-box .cancel, .page-rate-widget-box .cancel a{
    -moz-border-radius:  0 !important;
}

.page-rate-widget-box .cancel {
    background-color: transparent !important;
    border: none !important;
}

#u-author_block {
 display: none;
}
