[[/module]]

[[module CSS]]
.top-rated-box{
        float: right;
    border: 1px solid #600;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(102, 0, 0, .5);
    background: #fff;
    margin-left: 1rem;  
    padding: 1rem;
    padding-top: 0;
    padding-bottom: 0;  
    text-align: center;
}  

#page-content h1 {
    border-bottom: solid 0.08rem #660000;
    padding: 0 0 0.25em;
    margin-bottom: -0.5rem;
}
