display:none;
}
[[/module]]
[[html]]
<style>
@import url(http://d3g0gp89917ko0.cloudfront.net/v--de24f08b1628/common--theme/base/css/style.css);
@import url(http://d3g0gp89917ko0.cloudfront.net/v--de24f08b1628/common--theme/shiny/css/style.css);
@import url(http://scp-jp.wdfiles.com/local--theme/scp-sigma-9-off-canvas/style.css);
@import url(http://scp-jp.wdfiles.com/local--files/japanese-syntax/scp-WikidotCss.css);
</style>
<script type="text/javascript">
document.addEventListener("DOMContentLoaded",function(){
var nowURL = document.referrer;
var Tags ="";
var baseURL = "<a href='http://ja.scp-wiki.net/top-rated-pages";
var tagsOP = "";

var searchURL = "<a href='http://ja.scp-wiki.net/tag-search" + nowURL.substring(41, nowURL.length);
searchURL = searchURL.replace("/tags/","/tag/");
searchURL = searchURL.replace("/date/","/created_at/=");

if(nowURL.search("scp,scp-jp") >= 0){
document.getElementById("title1").innerText = "評価の高いSCP";
tagsOP = "/tags/scp,scp-jp,scp-ru,scp-ko,scp-cn,scp-fr,scp-pl,scp-es,scp-th,scp-de,scp-it/date/";
tags = "SCP記事";
}else if(nowURL.search("tale,tale-jp") >= 0){
document.getElementById("title1").innerText = "評価の高いTale";
tagsOP = "/tags/tale,tale-jp,tale-ru,tale-ko,tale-cn,tale-fr,tale-pl,tale-es,tale-th,tale-de,tale-it/date/";
tags = "Tale";
}else if(nowURL.search("-scp,-scp-jp") >= 0){
document.getElementById("title1").innerText = "その他評価の高い記事";
tags = "SCPとTale以外の記事";
tagsOP = "/tags/-scp,-scp-jp,-scp-ko,-scp-cn,-scp-th,-scp-fr,-scp-de,-scp-ru,-scp-pl,-scp-es,-scp-it,-tale,-tale-jp,-tale-ru,-tale-ko,-tale-cn,-tale-fr,-tale-es,-tale-pl,-tale-de,-tale-th,-tale-it/date/";
}else{
document.getElementById("title1").innerText = "評価の高い記事";
tagsOP = "/date/";
tags = "記事";
}

if(nowURL.search("/date/") >= 0){
var date = nowURL.substring(nowURL.indexOf("/date/") + 6, nowURL.length);
if(date.search("/p/") >= 0){
date = date.substring(0, date.search("/p/"));
}
if(date.length < 7){
searchURL = searchURL.replace(date, date.replace(date.substring(date.indexOf(".") , date.length), ".0" + date.substring(date.indexOf(".") + 1 , date.length) ) );
}
document.getElementById("title2").innerText = date.substring(0, 4) + "年";
if(date.length >= 5){
document.getElementById("title3").innerText = date.substring(date.indexOf(".") + 1, date.length) + "月の";
}else{
document.getElementById("title3").innerText = "の";
}
}else{
date = "";
}

var nowDate = new Date();
var nowYear = nowDate.getUTCFullYear();
var nowMonth = nowDate.getUTCMonth() + 1;

document.getElementById("link0").innerHTML = "今月作成された中で最も支持された" + tags + "を見るには、" + baseURL + tagsOP + nowYear + "." + nowMonth + "' target='_parent'>" + nowYear + "年" + nowMonth  + "月の"+ document.getElementById("title1").innerText + "</a>を参照してください。";
document.getElementById("link1").innerHTML = "リスト上に表示されているページについての更に詳細なデータを確認したい場合は、" + searchURL + "/order/rating desc/rating/>5/perPage/100/limit/10000' target='_parent'>" + "複数タグ検索システム</a>を利用して表示データを切り替えて下さい（SCPのメタタイトルなども表示可能です）。";
});

</script>
<div id="page-title">
<span id="title2"></span><span id="title3"></span><span id="title1"></span> 
</div>
<p>ここではサイトで最も好まれている記事を評価の多い順に表示しています。英語版サイトで最も好まれている記事を見たい場合は、<a href="https://scp-jp.wikidot.com/top-rated-pages-en" target="_top">評価の高い記事-EN</a>を参照してください。<span id="link0"></span></p>
<p id="link1"></p>
[[/html]]
それぞれのタグで評価の高い記事を確認したい際は[http://scp-jp.wikidot.com/top-rated-pages/tags/scp 評価の高いSCP]、[http://scp-jp.wikidot.com/top-rated-pages/tags/tale 評価の高いTale] 、[http://scp-jp.wikidot.com/top-rated-pages/tags/-scp,-tale その他の評価の高い記事]を参照してください。

[[collapsible show="また月別、年別で最も支持されたページを見るには、こちらの折りたたみ内のリストから選択して下さい。" hide="- 閉じる"]]
