[[html]]
<style>
@import url(http://d3g0gp89917ko0.cloudfront.net/v--de24f08b1628/common--theme/base/css/style.css);
@import url(http://d3g0gp89917ko0.cloudfront.net/v--de24f08b1628/common--theme/shiny/css/style.css);
@import url(http://scp-jp.wdfiles.com/local--theme/scp-sigma-9-off-canvas/style.css);
@import url(http://scp-jp.wdfiles.com/local--files/japanese-syntax/scp-WikidotCss.css);
</style>
<script type="text/javascript">
document.addEventListener("DOMContentLoaded",function(){
var nowURL = document.referrer;
var defaultLink1 = "<a href='/top-rated-pages";
var defaultLink2 = "評価の高い記事</a>";
var SCP1 = "<a href='http://scp-jp.wikidot.com/top-rated-pages/tags/scp";
var SCP2 = "評価の高いSCP</a>";
var Tale1 = "<a href='http://scp-jp.wikidot.com/top-rated-pages/tags/tale";
var Tale2 = "評価の高いTale</a>";
var Other1 = "<a href='http://scp-jp.wikidot.com/top-rated-pages/tags/-scp,-tale";
var Other2 = "その他の評価の高い記事</a>";
var Base1 = "' target='_parent'>";
var dateText = "";
var date = "";

if(nowURL.search("/date/") >= 0){
date = nowURL.substring(nowURL.indexOf("/date/"), nowURL.length);
var date1 = nowURL.substring(nowURL.indexOf("/date/") + 6, nowURL.length);
if(date1.search("/p/") >= 0){
date1 = date1.substring(0, date1.search("/p/"));
}
dateText = date1.substring(0, 4) + "年";
if(date1.length >= 5){
dateText += date1.substring(date1.indexOf(".") + 1, date1.length) + "月の";
}else{
dateText += "の";
}
}

if(nowURL.search("scp,scp-jp") >= 0){
document.getElementById("link1").innerHTML = defaultLink1 + date + Base1 + dateText + defaultLink2;
document.getElementById("link2").innerHTML = Tale1 + date + Base1 + dateText + Tale2;
document.getElementById("link3").innerHTML = Other1 + date + Base1 + dateText + Other2;
}else if(nowURL.search("tale,tale-jp") >= 0){
document.getElementById("link1").innerHTML = defaultLink1 + date + Base1 + dateText + defaultLink2;
document.getElementById("link2").innerHTML = SCP1 + date + Base1 + dateText + SCP2;
document.getElementById("link3").innerHTML = Other1 + date + Base1 + dateText + Other2;
}else if(nowURL.search("-scp,-scp-jp") >= 0){
document.getElementById("link1").innerHTML = defaultLink1 + date + Base1 + dateText + defaultLink2;
document.getElementById("link2").innerHTML = SCP1 + date + Base1 + dateText + SCP2;
document.getElementById("link3").innerHTML = Tale1 + date + Base1 + dateText + Tale2;
}else{
document.getElementById("link1").innerHTML = SCP1 + date + Base1 + dateText + SCP2;
document.getElementById("link2").innerHTML = Tale1 + date + Base1 + dateText + Tale2;
document.getElementById("link3").innerHTML = Other1 + date + Base1 + dateText + Other2;
}

});
</script>
<div style="text-align: center;">
<p><strong>&#171; <span id="link1"></span> | <span id="link2"></span> | <span id="link3"></span> &#187;</strong></p>
</div>
[[/html]]
