@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@300;400;700;900&display=swap');

div#page-content {
    color: #000;
    font-size: 0.9375rem;
    font-family: "Noto Sans SC", sans-serif;
}

li, p {
    line-height: 175%;
}

p {
    margin-block: 1.5em;
}

span.ruby {
    height: unset;
    display: inline-flex;
    flex-direction: column-reverse;
    align-items: center;
    vertical-align: top;
    text-indent: 0;
}

span.rt {
    display: block;
    margin-top: -0.625em;
    margin-bottom: 0;
    line-height: 1.25;
}


@media (max-width: 767px) {
div#page-content {
    font-size: 0.8125rem;
}
}

div#page-content h1,
div#page-content h2,
div#page-content h3,
div#page-content h4,
div#page-content h5,
div#page-content h6 {
    font-family: "Noto Sans SC", sans-serif;
    font-weight: 700;
}

div#page-content h1 {
    color: #000;
    font-weight: 700;
    text-align: center;
    margin: 4rem 0 2rem 0;
    border-top: solid 2px #CCC;
    border-bottom: solid 2px #CCC;
    letter-spacing: 0.25rem;
}

div#page-content > p,
div#page-content > div.blockquote > p {
    text-indent: 2em;
}

div#page-content h3 {
    border-left: double 6px #CCC;
    padding-left: 0.5rem;
}
