#title {
    margin: 0 0 .6em;
    border-color: #424248;
    border-color: rgb(var(--swatch-menubg-medium-dark-color,66,66,72));
    padding: 0 0 .25em;
    min-height: 2rem;
    color: #0c0c0c;
    font-family: Poppins,sans-serif;
    font-family: var(--title-font);
    font-weight: 600;
    text-align: left;
