[[include :scp-jp:theme:black-highlighter-theme]]
[[include :scp-jp:component:bhl-dark-sidebar]]
[[include :scp-jp:component:centered-header-bhl]]
[[include :scp-jp:component:toggle-sidebar-bhl]]
[[include :scp-jp:component:fade-in speed=4]]
[[module CSS]]
:root {
    --timeScale: 6;
    --timeDelay: 6s;
    --red: 176, 216, 247;
     --medium-accent: var(--red);
     --cutColour: #fafafa;
     --barColour: var(--red);
     --toggle-border-color: rgb(var(--red));
     --toggle-icon-color: rgb(var(--red));
     --mono-font: 'IBM Plex Mono', monospace;
     --background-gradient-distance: 20rem;
     --background-gradient-color: var(--red);
     --dark-accent: var(--red);
     --barColour: var(--red);
     --bright-accent: var(--red);

--link-color: 140, 199, 245;
--visited-link-color: 176, 216, 247;
--hover-link-color: 176, 216, 247;
}

#page-content hr {
border-top: none;
height: .09em;
background: rgb(var(--red));
border-left: 50px double #fcfcfc;
border-right: 50px double #fcfcfc;
}

.info-container{
--barColour: #b0d8f7;
}

.scp-image-block {
border: none;
border-bottom: 2px solid #b0d8f7;
    box-shadow: none;
}

.info-container .collapsible-block-folded a{
color: #FFF;}
.info-container .collapsible-block-unfolded-link a{
color: #FFF;
}

.info-container .collapsible-block-folded a{
color: #FFF;}
.info-container .collapsible-block-unfolded-link a{
color: #FFF;
}

#page-content > .collapsible-block {
    display: block;
    text-align: center;
    padding: .8em;
    background-color: #e3e3e3;
    border-top: 0.125rem dashed #b5b5b5;
    border-bottom: 0.125rem dashed #b5b5b5;
}

#page-content > .collapsible-block .collapsible-block-content {
    text-align: left;
}

blockquote,
div.blockquote {
    border: 0.125rem solid #d4d4d4;
    background-color: #F5F5F5;
}

.interview{
    border-top:solid 2px #b0d8f7;
    border-bottom:solid 2px #b0d8f7;
    background:#F5F5F5;
    width:90%;
    padding:1px 15px;
    margin: 10px auto;
}

.addendum{
    border:solid 1px #5e5e5e;
    background:#ffffff;
    width:90%;
    padding:1px 15px;
    margin: 10px auto;
}

.poster{
    border:solid 2px #9c0202;
    background:#ffffff;
    width:60%;
    padding:15px;
    margin: 10px auto;
}

#page-title{display:none}

[[/module]]
[[module css]]
:root {
    --logo-image: url("http://scp-jp-storage.wikidot.com/local--files/file:6444668-110-5zrm/3V7s7U0_d.png");
    --header-title: "アニマル財団";
    --header-subtitle: "ダックウィーク！";
}
[[/module]]

