[[module css]]
/* CSS shamelessly stolen from PlaguePJP's author page */

@import url('https://fonts.googleapis.com/css2?family=Lexend:wght@100..900&display=swap');

:root {
  --accentColor: #9a9afe;
}

.banner {
  width: 100%;
  color: white;
  font-size: 3em;
  padding: 15px 0; 
  text-align: center; 
  font-family: 'Lexend', sans-serif;
  border-top: 1.5px solid white; 
  border-bottom: 1.5px solid white; 
}

.main {
    display: block;
    height: 18em;
    padding-left: 0.75rem;
    color: white;
    border-radius: 3px;
    margin: -1em;
    text-align: center; 
}

.art {
    background-position: 25% 40%;
    background-size: 140%;
    transition: all 200ms ease-in-out;
    color: black! important;
    padding: -30px;
    text-align: center; 
}

.art:hover {
    transition: all 200ms ease-in-out;
    background-color: transparent;
    box-shadow: 0px 0px 9px 3px rgba(0,0,0,0.5);
    mix-blend-mode: none;
    background-color: none;
    text-shadow: #051c2e 0px 0 3px;
}

/* SCP name */
.name {
    font-family: 'Lexend', sans-serif;
    font-size: 2.5em;
    font-weight: 700;
    letter-spacing: -1px;
    position: relative;
    color: black;
    text-align: center; 
    text-shadow: 0 1px 3px rgba(0,0,0,.2);
}

.art:hover .name {
    color: #B0B0F7!important;
    transition: all 25ms ease-in-out;
}

/* SCP title */
.subtitle {
    font-family: 'Lexend', sans-serif;
    font-weight: 200;
    color: black;
    font-size: 1.5em;
    position: relative;
    font-style: italic;
    top: -0.7em;
    text-align: center; 
    text-shadow: 0 1px 3px rgba(0,0,0,.2);
}

.art:hover .subtitle {
    color: white!important;
}


.info {
    font-family: 'Lexend', sans-serif;
    font-size: 1.1em;
    font-style: italic;
    color: black;
    position: relative;
    top: -1.2em;
    font-weight: 200;
    line-height: 21px;
    text-align: center; /* Center the text inside .info */
}

.art:hover .info {
    color: white!important;
}

.proposal {
    font-family: 'Lexend', sans-serif;
    font-weight: 200;
    font-size: 1.1em;
    position: relative;
    top: -1.2em;
    font-style: italic;
    color: black;
    line-height: 21px;
    text-align: center; 
    text-shadow: 0 1px 3px rgba(0,0,0,.2);
}

.art:hover .proposal {
    color: gold;
}

.rate {
    text-align: center; 
    position: relative;
    top: -1.5em;
    color: #0a3e66;
    text-shadow: 1px 1px 0px black;
}

[[/module]]

