[[module CSS]]

/*
    Fossilize Theme
    [2023 Wikidot Theme]
    By EstrellaYoshte

    Based on:
    Cafe Theme by EstrellaYoshte
    Sunside Theme by EstrellaYoshte
    Generic Space Theme by EstrellaYoshte

    Revised by renerd

*/

:root{
    --header-title: "SITE-81UT";
    --sp_header-logo: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A2081491-189-1ma1/site-81UT.svg);
    --fossil-header: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A6442226-116-97z5/ueno-background.jpg);
    --fossil-footer: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A6442226-116-97z5/ueno-background.jpg);
}

#page-title {
    display: none;
}

body {
    background-image: initial;
}

@media (min-width: 769px) {
    p, li, td {
        line-height: 1.6em;
        font-size: 1.05em;
    }
}

/*-----RATE MODULE------*/

div.rate-box-with-credit-button {
    border-radius: 0.3rem;
    background-color: #0C0C0A !important;
}

div.rate-box-with-credit-button .page-rate-widget-box {
    padding: 0px 0px 0px 5px;
}

div.rate-box-with-credit-button .page-rate-widget-box {
    background-color: #0C0C0A;
}

div.page-rate-widget-box .rate-points {
    background-color: transparent !important;
    color: #E4CCA7;
}

.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
    padding: 2px 10px !important;
    color: #E4CCA7;
}

.page-rate-widget-box .cancel a {
    color: #E4CCA7 !important;
}

div.page-rate-widget-box, div.rate-box-with-credit-button {
    background-color: #0C0C0A;
}

.rate-box-with-credit-button .creditButton {
    background-color: #0C0C0A !important;
    border: #0C0C0A !important;
}

div.modalbox {
    background: #E4CCA7;
}

.modalbox .modalbox-title {
    background:  #0C0C0A !important;
}

.modalbox .modalbox-title  h2 {
    color: #E4CCA7;
}

/*-----SIDE BAR------*/

#side-bar {
    background-color: #0C0C0A;
    border:  solid #0C0C0A;
    color: #E4CCA7;
}

#side-bar .side-block {
    background-color: #0C0C0A !important;
}

:is(#interwiki, #side-bar) .heading {
    color: #E4CCA7);
    font-weight: bold;
}

:is(#interwiki, #side-bar) .side-block a {
    color: #E4CCA7;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #E4CCA7;
}

/*-----SCROLLBAR------*/

::-webkit-scrollbar-thumb {
    background: #AFAFAF;
}

::-webkit-scrollbar-track {
    background: #E5E5E5;
}

.today {
    margin-left: 10px;
    margin-top: 5px;
    padding: 1px 15px;
    border-left: solid 3px #407F60
}

.moji {
    text-align: center;
}

@media (min-width: 769px) {
    .moji {
        font-size: 120%;
    }
}


[[/module]]

