[[module css]]
@import url(http://scp-jp.wdfiles.com/local--files/uksvg-jp/welcomebox.css);
@import url(http://scp-jp.wdfiles.com/local--files/uksvg-jp/thanatomania.css);
@import url(http://scp-jp.wdfiles.com/local--files/uksvg-jp/prosopagnosia.css);
@import url(http://scp-jp.wdfiles.com/local--files/uksvg-jp/dialogue-dept.css);
@import url(http://scp-jp.wdfiles.com/local--files/uksvg-jp/kyouikaireki.css);
@import url(http://scp-jp.wdfiles.com/local--files/uksvg-jp/meteorological-dept.css);

#page-content {
  font-family: "M PLUS 1p", sans-serif;
}

#page-content .uksvgbox {
  width: 90vw;
  max-width: 500px;
  height: 90vw;
  max-height: 500px;
  background-repeat: no-repeat;
  margin: 0 auto;
  background-size: contain;
}

#page-content .uksvgbox.welcomebox {
  width: 90vw;
  max-width: 600px;
  height: 27vw;
  max-height: 180px;
}

#page-content .uksvgbox.kyouikaireki {
  width: 90vw;
  max-width: 600px;
  height: 27vw;
  max-height: 180px;
}

#page-content .customcol {
  --accent: gray;
}

#page-content .customcol .collapsible-block-folded a.collapsible-block-link {
  color: var(--accent);
  font-size: 120%;
  font-weight: bold;
}

#page-content .customcol .collapsible-block-folded a.collapsible-block-link::after {
  content: "\A（クリックで開きます）";
  white-space: pre;
  color: gray;
  font-size: 10px;
  text-decoration: none !important;
}

#page-content .customcol .collapsible-block-folded a.collapsible-block-link::after:hover {
  text-decoration: none !important;
}

#page-content .customcol .collapsible-block-folded a.collapsible-block-link {
  color: var(--accent);
  font-size: 150%;
  font-weight: bold;
}

#page-content .customcol .collapsible-block-unfolded {
  width: 100%;
  border: solid 0.5px var(--accent);
  margin-left: auto;
  margin-right: auto;
  position: relative;
}

#page-content .customcol .collapsible-block-unfolded-link {
  width: 100%;
  position: absolute;
  top: 0;
  left: 0;
  background: var(--accent);
  line-height: 2;
  font-size: 120%;
  font-weight: bold;
}

#page-content .customcol .collapsible-block-unfolded-link a {
  color: white;
  text-decoration: none !important;
  width: 100%;
  display: block;
}

#page-content .customcol .collapsible-block-content {
  margin: 3em 1em 1em 1em;
}

[[/module]]

[[/iftags]]

