[[div class="uksvgbox {$t}"]]
@@ @@
[[/div]]
[[module css]]
@import url(http://scp-jp.wdfiles.com/local--files/uksvg-jp/{$t}.css);

#page-content .uksvgbox {
  max-width: 500px;
  max-height: 500px;
}

#page-content .uksvgbox {
  width: 90vw;
  max-width: {$width};
  height: 90vw;
  max-height: {$width};
  background-repeat: no-repeat;
  margin: 0 auto;
  background-size: contain;
}

#page-content .uksvgbox.kyouikaireki {
  max-width: 600px;
  max-height: 180px;
}

#page-content .uksvgbox.kyouikaireki {
  width: 90vw;
  max-width: {$width};
  height: 27vw;
  max-height: calc({$width} / 0.3);
}

#page-content .uksvgbox {
  background-image: var(--uksvg-{$t});
}
[[/module]]
[[/iftags]]
