[[module CSS]]
.info-container{
--barColour: rgb(var(--dark-accent));
--linkColour: rgb(var(--bright-accent));
}

.sign {
    color: white;
}

.ature, .ature a, .ature a:visited {
    color: rgb(var(--bright-accent));
}

.personnel {
  border: 2px solid rgb(var(--bright-accent));
  padding: 0rem 1rem;
  background: rgb(var(--dark-accent));
  color: rgb(var(--bright-accent));
  margin: 0.75rem;
}

.list-pages-box{
width:100%;
display: flex;
flex-flow: row wrap;
justify-content: space-evenly;
}
.article {
  border: 2px solid rgb(var(--bright-accent));
  padding: 0rem 1rem;
  background: rgb(var(--dark-accent));
  color: rgb(var(--bright-accent));
  margin: 0.75rem;
  width: fit-content;
  font-family: var(--title-font);
}

.article a, .article a:visited{
    color: rgb(var(--bright-accent));
}

/* -- CARDS -- */

.flip-card {
  background-color: transparent;
  width: 33.5rem;
  height: 22.4rem;
  perspective: 1000px;
  margin-top: 1rem;

  border-radius: 2.25rem;
}

.flip-card:nth-of-type(odd) {
  margin-left: 27%;
}

.flip-card-inner {
  position: relative;
  width: 100%;
  height: 100%;
  text-align: center;
  transition: transform 0.8s;
  transform-style: preserve-3d;
}

.flip-card:hover .flip-card-inner {
  transform: rotateX(180deg);
}

.flip-card-front, .flip-card-back {
  position: absolute;
  width: 100%;
  height: 100%;
  -webkit-backface-visibility: hidden; /* Safari */
  backface-visibility: hidden;
}

.flip-card-front {
  background-image: url('http://scptestwiki.wikidot.com/local--files/yurtest2/KofHDarkSide.png');
  background-size: contain;
  background-repeat: no-repeat;
  border-radius: 2.25rem;
  border: 2px solid gold;
  background-color: #0000;
  background-blend-mode: overlay;
  text-shadow: 2px 2px 2px #1c1c1c;
}

.flip-card-back {
  background-image: url('https://scp-wiki.wdfiles.com/local--files/scp-7666/luxorred.png');
  background-size: cover;
  background-repeat: no-repeat;
  color: white;
  transform: rotateX(180deg);
  border-radius: 2.25rem;
  box-shadow: 0px 0px 10px 3px #6f1a1a;
}

.cardtext {
  top: 30%;
  position: absolute;
  left: 10%;
  right: 10%;
}

.flip-card-front .cardtext p {
   margin-top: 0;
}

.cardtext a { color: white!important; font-size: 120%;}
.votes { color: rgb(255, 215, 0);}
.comments { color: rgb(255, 215, 0);}
.flip-card-front .cardtext {
   font-size: 125%;
}
.flip-card-back .cardtext {
   font-style: italic;
}
.flip-card:nth-of-type(2n) .flip-card-front { background-image: url('http://scptestwiki.wdfiles.com/local--files/yurtest2/QoC.png');}

@media only screen and (max-width: 1024px) {
    .flip-card {
        margin: 1rem auto;
        width: 22.5rem;
        height: 15rem;
    }
    .flip-card:nth-of-type(odd) {
        margin-left: auto!important;
    }
}
[[/module]]

