  background-repeat: no-repeat;
  border-radius: 2.25rem;
  border: 2px solid gold;
  background-color: #0000;
  background-blend-mode: overlay;
  text-shadow: 2px 2px 2px #1c1c1c;
}

.flip-card-back {
  background-image: url('https://scp-wiki.wdfiles.com/local--files/scp-7666/luxorred.png');
  background-size: cover;
  background-repeat: no-repeat;
  color: white;
  transform: rotateX(180deg);
  border-radius: 2.25rem;
  box-shadow: 0px 0px 10px 3px #6f1a1a;
}

.cardtext {
  top: 30%;
  position: absolute;
  left: 10%;
  right: 10%;
}

.flip-card-front .cardtext p {
   margin-top: 0;
}

.cardtext a { color: white!important; font-size: 120%;}
