@import url('https://fonts.googleapis.com/css?family=Caveat|Cinzel|Source+Serif+Pro&display=swap');

.arcnote {
    border: solid 1px black;
    margin: 10px 18%;
    padding: 1em 4%;
    background: #fffdd0 none;
    }

.shimbunag, .shimbunag h2, .shimbunag h3 {
    font-family: 'Source Serif Pro', serif;
}

.shimbunag {
    border: solid 1px black;
    margin: 10px 4%;
    padding: 0px 3%;
    background: #f4ffed none;
    }

.shimbunag .midashi {
    color: black;
    background: none #e9ffdb;
    border: ridge 20px green;
    font-weight: bold;
    font-size: 300%;
    text-align: center;
    margin-bottom: 1em;
    }

@media (min-width: 800px) {
   .coll {
       display: flex;
       flex-direction: row;
       width: 100%;
   }
