       width: 50%;
       padding: 0px 1% 0px 2%;
   }
   .hidari {
       border-style: none solid none none;
       border-width: 1px;
       border-color: black;
   }
}

.cavmark {
    color: red;
    font-family: 'Caveat', cursive;
    font-size: 1.7em;
    }

.tetyo {
    border: solid 1px black;
    margin: 10px 8%;
    padding: 0px 1% 0.8em 1%;
    }

.headline {
    font-size: 1.2em;
    font-weight: bold;
    font-style: italic;
    }

.tegami {
    border: solid 1px black;
    margin: 1em 10%;
    padding: 0.8em 1%;
    }

.exhdai {
    margin: 2.1em 11% 4em 11%;
    text-align: center;
    font-size: 150%;
    font-weight: bold;
    /* for Western languages translation:
    font-family: 'Cinzel', serif;
    */
    background: #fffdd0 none;
    padding: 0.5em 1%;
    border: black solid 1px;
}

/* お試し長ダッシュ*/
.nagadash {
    display: inline-block;
    transform-origin: left bottom; -webkit-transform-origin: left bottom; margin-right: 1.5em;
    transform: scale( 2.2 , 1.0 ); -webkit-transform: scale( 2.2 , 1.0 ); -ms-transform: scale( 2.2 , 1.0 );
    }
