[[module CSS]]
.terminalbody {
    width: 100%;
    padding: 0.25rem;
    box-sizing: border-box;
    border: solid 0.15rem black;
    border-radius: 0.85rem;
    background-color:#F7F7F7;
}
.terminalbody hr {
    margin: 1.25rem 0;
    background-color: black;
    height: 0.15rem;
}

.mailend {
    color: white;
    background-color:#990000;
    display: flex;
    align-items: center;
    height: 2.65rem;
    width: 100%;
    box-sizing: border-box;
    font-weight: bold;
    font-size: 125%;
    padding: 0 0.75rem;
}
.mailend.start {
    border-radius: 0.5rem 0.5rem 0 0;
}
.mailend.end {
    border-radius: 0 0 0.5rem 0.5rem;
    justify-content: flex-end;
}
.mailend.end a {
    padding: 0.25rem 0.5rem;
    color: #990000;
    background-color: #F7F7F7;
    box-shadow: 0 0 0 0 #F7F7F7;
    border-radius: 0.75rem;
    box-sizing: border-box;
    font-size: 80%;
    transition: all 0.15s ease-out;
}
.mailend.end a:hover {
    text-decoration: none;
    color: #F7F7F7;
    background-color: #990000;
    box-shadow: 0 0 0 0.125rem #F7F7F7;
}

.mailbody {
    padding: 0.1rem 1rem;
    overflow-y: auto;
}
.tofrom {
    margin-left: 1rem;
    margin-top: 0.25rem;;
    padding: 0 1rem;
    border-left: solid 0.25rem #990000;
    font-size: 105%;
}
[[/module]]

