[[module CSS]]
.content-panel .content-type-title > p {
  margin: 0;
}
.content-panel .content-type-title {
  font-size: 120%;
  font-weight: bold;
  padding: 5px 20px;
  background-color: #666;
  color: #fff;
  border-radius: 8px 8px 0 0;
  box-shadow: inset 0 1px 1px rgba(255,255,255,.8),
    inset 0 15px 1px rgba(255,255,255,.2),
    inset 0 15px 10px rgba(255,255,255,.2);
}
.content-panel .content-type-description {
  padding: 0 20px;
  text-align: justify;
}
.content-panel.content-row .content-type-description {
  padding: 5px 20px;
}

.list-pages-box > table {
    width: 100%;
}
.list-pages-box th {
    padding: 0 2px;
    background-color: #666;
    color: #fff;
    font-weight: bold;
}
.list-pages-box td {
    vertical-align: top;
}
.list-pages-box th:not(:first-child),
.list-pages-box td:not(:first-child) {
    text-align: center;
}
.list-pages-box tr > td:first-child > div {
    display: -webkit-flex;
    display: flex;
    -webkit-align-items: center;
    align-items: center;
}
.list-pages-box tr > td:first-child > div > div:first-child {
    -webkit-flex-grow: 1;
    flex-grow: 1;
}
.list-pages-box tr > td:first-child > div img.wanderers {
    min-width: 16px;
    width: 16px;
    height: 11px;
    box-shadow: 0 1px 3px rgba(0,0,0,.5);
}
.list-pages-box tr > td:first-child > div > div:not(.wanderers):not(.wanderers-adult) > img.wanderers {
    display: none;
}

span.rating-tag {
    display: inline-block;
    padding: 0.125rem 0.625rem;
    border-radius: 0.3125rem;
    text-decoration: none;
    background-color: #633;
    color: #fff;
    cursor: pointer;
}
span.rating-tag.ratinga {
    background-color: #93F293;
}
span.rating-tag.ratinga::before {
    content: "合格";
}
span.rating-tag.ratingb {
    background-color: #FFA500;
}
span.rating-tag.ratingb::before {
    content: "高分";
}
span.rating-tag.ratingc {
    background-color: #0033AA;
}
span.rating-tag.ratingc::before {
    content: "争议";
}
span.rating-tag.rating {
    background-color: #aacccc;
}
span.rating-tag.rating::before {
    content: "低评";
}
span.rating-tag.ratingl {
    background-color: #a7a7a7;
}
span.rating-tag.ratingl::before {
    content: "冷门";
}
span.rating-tag + div[id*="hovertip"] {
    display: none;
}
[[/module]]

