content:
[[iftags +组件后端]]
[[note]]
使用此组件参考[[[reference:classification-header-iti|]]]。
[[/note]]
[[/iftags]]

[[div class="classification-header clearance{$clearance} [[#if {$hralt} | hralt | ]]"]]
    [[div_ class="left-box"]]
        [[div_ class="left-top-box"]]
            **{$item-number-title}:** {$item-name}
        [[/div]]
        [[div_ class="left-bottom-box"]]
            **{$object-class-title}:** {$object-class}
        [[/div]]
    [[/div]]
    [[div class="right-box"]]
        [[div_ class="bar-container"]]
            [[div_ class="bar"]]
            [[/div]]
            [[div_ class="bar"]]
            [[/div]]
            [[div_ class="bar"]]
            [[/div]]
            [[div_ class="bar"]]
            [[/div]]
            [[div_ class="bar"]]
            [[/div]]
            [[div_ class="bar"]]
            [[/div]]
        [[/div]]
        [[div_ class="right-container"]]
            [[div_ class="right-top-box"]]
                {$clearance-text}
            [[/div]]
            [[div_ class="right-bottom-box"]]
                {$restriction}
            [[/div]]
        [[/div]]
    [[/div]]
[[/div]]

[[module CSS]]
:root {
    --level0: 51,51,51;
    --level5: 187,0,17;
    --level6: 255,0,17;
}
.classification-header {
    display: flex;
    justify-content: space-between;
    margin: 0 auto;
    color: rgb(var(--level0));
    white-space: pre;
}
.classification-header .right-box {
    display: flex;
    justify-content: flex-end;
}
.classification-header .left-box,
.classification-header .right-box .right-container {
    display: flex;
    flex-direction: column;
    color: rgb(var(--level0));
}
.classification-header .left-box .left-top-box,
.classification-header .left-box .left-bottom-box,
.classification-header .right-box .right-container .right-top-box,
.classification-header .right-box .right-container .right-bottom-box {
    flex: 1;
    display: flex;
    align-items: center;
    line-height: 1.75em;
    color: rgb(var(--level0));
}
.classification-header .left-box .left-top-box,
.classification-header .left-box .left-bottom-box {
    justify-content: flex-start;
}
.classification-header .right-box .right-container .right-top-box,
.classification-header .right-box .right-container .right-bottom-box {
    justify-content: flex-end;
}
.classification-header.clearance6 .right-box .right-bottom-box em {
    font-style: unset;
    transform: scale(1, 1.25);
    font-size: 80%;
}
.classification-header .right-box .bar-container {
    display: flex;
    flex-direction: row-reverse;
    align-items: center;
    transform: skewX(15deg);
    transform-origin: 0 100%;
}
.classification-header .bar-container .bar {
    display: none;
    width: 0.5em;
    height: 2.25em;
    background: rgb(var(--level0));
    margin-left: 0.25em;
}
.classification-header .bar-container .bar:nth-last-child(5) {
    background: rgb(var(--level5));
}
.classification-header .bar-container .bar:nth-last-child(6) {
    background: rgb(var(--level6));
}
.classification-header.clearance0 .bar-container .bar {
    outline: dashed rgb(var(--level0)) 1px;
    background: transparent;
}
.classification-header.clearance0 .bar-container .bar:nth-last-child(1),
.classification-header.clearance1 .bar-container .bar:nth-last-child(1),
.classification-header.clearance2 .bar-container .bar:nth-last-child(1),
.classification-header.clearance3 .bar-container .bar:nth-last-child(1),
.classification-header.clearance4 .bar-container .bar:nth-last-child(1),
.classification-header.clearance5 .bar-container .bar:nth-last-child(1),
.classification-header.clearance6 .bar-container .bar:nth-last-child(1) {
    display: block;
}
.classification-header.clearance2 .bar-container .bar:nth-last-child(2),
.classification-header.clearance3 .bar-container .bar:nth-last-child(2),
.classification-header.clearance4 .bar-container .bar:nth-last-child(2),
.classification-header.clearance5 .bar-container .bar:nth-last-child(2),
.classification-header.clearance6 .bar-container .bar:nth-last-child(2) {
    display: block;
}
.classification-header.clearance3 .bar-container .bar:nth-last-child(3),
.classification-header.clearance4 .bar-container .bar:nth-last-child(3),
.classification-header.clearance5 .bar-container .bar:nth-last-child(3),
.classification-header.clearance6 .bar-container .bar:nth-last-child(3) {
    display: block;
}
.classification-header.clearance4 .bar-container .bar:nth-last-child(4),
.classification-header.clearance5 .bar-container .bar:nth-last-child(4),
.classification-header.clearance6 .bar-container .bar:nth-last-child(4) {
    display: block;
}
.classification-header.clearance5 .bar-container .bar:nth-last-child(5),
.classification-header.clearance6 .bar-container .bar:nth-last-child(5) {
    display: block;
}
.classification-header.clearance6 .bar-container .bar:nth-last-child(6) {
    display: block;
}
.classification-header.hralt + hr {
    height: 4px;
    margin: 0;
    background: linear-gradient(rgb(var(--level0)) 0 2px,#0000 2px 3px,rgb(var(--level0)) 3px);
}
[[/module]]
