[[module CSS arguments...]]
#page-title {
display: none;
}
.forClickChangePageTitleDiv {
font-size: 200%;
color: #990011;
padding: 0 0 0.25em;
margin: 0 0 0.6em;
border-bottom: 1px solid #bbb;
font-weight: normal;
}

.forClickChangePageTitleDiv .collapsible-block-link{
color: #990011;
padding: 0;
margin: 0;
font-weight: normal;
text-decoration: none;
}

.forClickChangePageTitleDiv .collapsible-block-link:hover{
color: #990011;
padding: 0;
margin: 0;
font-weight: normal;
text-decoration: none;
}

.forClickChangePageTitleDiv p {
padding: 0;
margin: 0;
}
[[/module]]
[[module ListPages separate="no" limit="1" range="."]]
[[div class="forClickChangePageTitleDiv"]]
[[collapsible show="%%title%%" hide="ヒツジの絵を描いたよ！"]]
[[/collapsible]]
[[/div]]
[[/module]]

