:root {
    --fade-in-delay: 0.05s;
}

/* SLIDE SHOW - BASIC FORMAT FOR VIEWPORT */
.yui-navset .yui-nav li, .yui-navset .yui-navset-top .yui-nav li {
    flex-grow: 0 !important;
}

.yui-navset-top {
    container-type: inline-size;
}

.yui-nav {
    display: flex;
    justify-content: space-between;
    align-items: baseline;
    bottom-border-width: 0px;
}

.yui-nav li {
    display: none !important;
    margin: 0 !important;
}

.yui-nav li.selected {
    display: inline-block !important;
}

.yui-content {
    display: grid;
    padding: 0em !important;
    min-width: 110%;
    margin-left: -5%;
    max-width: 120%;
    border: 2px solid #333 !important;
}

@container (width >= 1em) {
    .yui-content {
        min-height: calc(60cqw * 1.1);
        max-height: calc(60cqw * 1.1);
        margin-top: -73cqw;
    }
    .yui-nav {
        margin-top: 69cqw !important;
}
}

.debugNote {
    margin: auto;
    display: none;
}

@media (max-width: 600px) {
    .debugNote {
        display: block;
}

}

/* Slide Visuals CSS */
.slideContainer {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr 1fr 1fr;
    height: 100%;
    width: 100%;
    background-size: cover;
    background-position: center;
    background-image: url(https://scp-wiki.wdfiles.com/local--files/kill-agents-and-you/2560px-Pseudo_Kleinian_OpenCL_214854124_25K.jpg);
    background-blend-mode: saturation;
    background-color: #00000040;
    color:white;
}

.slideContainer p, .slideContainer li {
    font-size: 1.3em;
    line-height: 1.6;
}

.slideAInner {
    grid-area: 1 / 1 / 5 / 5;
    margin: auto 1.5em;
    padding: 0.5em 1em;
    border: solid 1px black;
    background: rgb(210 210 210 / 30%);
}

.slideBInnerLeft {
    grid-area: 1 / 1 / 5 / 3;
    margin: 1.5em 0.5em 1.5em 1.5em;
    padding: 0.5em 1em;
    border: solid 1px black;
    background: rgb(210 210 210 / 30%);
}
.slideBInnerRight {
    grid-area: 1 / 3 / 5 / 5;
    margin: 1.5em 1.5em 1.5em 0.5em;
    padding: 0.5em 1em;
    border: solid 1px black;
    background: rgb(210 210 210 / 30%);
}

.slideCInner {
    grid-area: 2 / 1 / 4 / 5;
    margin: auto 1.5em;
    padding: 0.5em 1em;
    border: solid 1px black;
    align-content: center;
    text-align: center;
    background: rgb(210 210 210 / 30%);
}

.slideDInner {
    grid-area: 5 / 1 / 5 / 5;
    margin: auto 1.5em;
    padding: 0.5em 1em;
    border: solid 1px black;
    background: rgb(210 210 210 / 30%);
}

.slideEInnerLeft {
    grid-area: 1 / 1 / 5 / 1;
    margin: 1.5em -1.5em 1.5em 1.5em;
    padding: 0.5em 1em;
    border: solid 1px black;
    border-right-width: 0px;
    background: rgb(210 210 210 / 30%);
}
.slideEInnerRight {
    grid-area: 1 / 2 / 5 / 5;
    margin: 1.5em;
    padding: 0.5em 1em;
    border: solid 1px black;
    background: rgb(210 210 210 / 30%);
}

.slideFInnerTitle {
    grid-area: 1 / 1 / 2 / 5;
    margin: 1.5em 1.5em 0em 1.5em;
    padding: 0.5em 1em;
    border: solid 1px black;
    background: rgb(210 210 210 / 30%);
    text-align:center;
}
.slideFInnerBody {
    grid-area: 2 / 1 / 5 / 5;
    margin: 1em 1.5em 1.5em 1.5em;
    padding: 0.5em 1em;
    border: solid 1px black;
    background: rgb(210 210 210 / 30%);
}
.slideGInnerTitle {
    grid-area: 1 / 1 / 2 / 5;
    margin: auto 1.5em 0em 1.5em;
    padding: 0.5em 1em;
    border: solid 1px black;
    background: rgb(210 210 210 / 30%);
    text-align:center;
}
.slideGInnerLeft {
    grid-area: 2 / 1 / 5 / 3;
    margin: 1em 0.5em auto 1.5em;
    padding: 0.5em 1em;
    border: solid 1px black;
    background: rgb(210 210 210 / 30%);
}
.slideGInnerRight {
    grid-area: 2 / 3 / 5 / 5;
    margin: 1em 1.5em auto 0.5em;
    padding: 0.5em 1em;
    border: solid 1px black;
    background: rgb(210 210 210 / 30%);
}

.titleSlide {
    grid-area: 2 / 1 / 4 / 5;
    margin: auto 1.5em;
    padding: 0.5em 1em;
    align-content: center;
    text-align: center;
    background: rgb(0 0 0 / 30%);
}

/* Individual Slide Logic */
.yui-nav:nth-child(1) {

/*Slide 1*/
li.selected:nth-of-type(1) + li:nth-of-type(2) {
    display: inline-block !important;
}

/*Slide 2*/
li:nth-of-type(1):has(+ li.selected:nth-of-type(2)){
