[[module CSS]]
/* 图书馆版式复位 */
#side-bar {
    background-color: grey !important;
    box-shadow: inherit;
    border-radius: inherit;
}
#side-bar .side-block{
    background-color: rgba(0,0,0,0) !important;
}
#side-bar .heading {
    width: inherit;
    margin: inherit;
}
span:not([class]):not([style]) > sup.footnoteref:first-child > a.footnoteref:before,
sup.footnoteref > a.footnoteref:before {
content: inherit;
}
span:not([class]):not([style]) > sup.footnoteref > a.footnoteref:before {
content: inherit;
}
iframe.scpnet-interwiki-frame {
  filter: inherit;
  -webkit-filter: inherit;
}
[[/module]]

[[include :scp-wiki-cn:theme:black-highlighter-theme]]
