[[module css]]

#header #header-extra-div-1, #header #header-extra-div-2 {
    top: 1rem;
}

.page-note {

  writing-mode:  vertical-rl;

    display: block;
/*    overflow: hidden;*/
    font-family: kaiti,"Monotype Corsiva", "Bradley Hand ITC", sans-serif;
    font-style: normal;
    background-attachment: scroll;
    background-clip: border-box;
    background-color: #e7e9dc;
    background-image: linear-gradient(to right , rgb(231, 233, 220) 2%);
    background-origin: padding-box;
    background-position: 0px 10px;
    background-repeat: repeat;
    background-size: 100% 10px;

    border: 1px solid #CCC;
    /*border-radius: 10px;*/
    padding: 2em 3em 0 0px;
    /*box-shadow: 0px 1px 3px rgb(0, 0, 0, 0.2); 不要使用阴影*/

}

.books-1-start a{
padding:0.4em 0 0.4em 0;
}

.poem {
    line-height: 20px;
    margin: 2em 0 ;
       font-size: 220%;
}

.phone{
 display:none;
}

#page-title{
display:none;
}

 @media (max-width: 479px) {
#page-title{
display:block;
}
.ff-pc{
display:none;
}
.phone{
display:block;
text-align: center;
font-size: 150%;
font-family: 'Andale Mono', 'Courier New', Courier, monospace;   

}
}
[[/module]]
