[[module css]]
@import url("https://cdn.jsdelivr.net/gh/Crimone/crimson-anemone@1.0.0/noto-serif-sc-300.css");

#container {
    background-image: linear-gradient(to bottom, rgba(0,0,0,0.6) 0%, rgba(0,0,0,0.7) 50%, rgba(0,0,0,0.9) 100%), url(https://pic.imgdb.cn/item/63e0986e4757feff33eef5ab.jpg);
    background-size:cover;
    background-attachment: fixed, fixed;
    background-position: center;
    position: relative;
}

div#content-wrap {
    margin-top: 0;
}

/* 透明 */
#page-options-container {
    opacity: 0.1;
    transition: ease-in 0.1s;
}

/* 鼠标放在上面变回不透明 */
#page-options-container:hover {
    opacity: 1;

}

body{
background:#000;
font-family:"Noto Serif SC";
color:#AFADA7;
font-size:19px;
}
p {
    line-height:230%;
}

#container-warp {
    max-width:2000px;
}

a {
    color: white;
    color: white;
}
a:visited {
    color: white;
    color: white;
}

#header h1 a::before {
    color: transparent;
}

#header h2 span::before {
    color: transparent;
}

#header {
    background-image: url( );
    background-size: 6rem;
}

div#container-wrap {
background: url() repeat;}

.meta-title {
display:none
}

.page-tags,#search-top-box,#top-bar,#side-bar,#login-status,#page-title,#header,#footer,#license-area,#footer,#license-area  {
display:none
}
#footer{
opacity:0;
}
#page-content {
    min-height: 720px;
}

#main-content {
    margin:0 5% 0  5%;
}

.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .cancel {
    background-color: transparent !important;
    border-color: transparent;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
    color: #FFFFFF;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover,
.page-rate-widget-box .cancel a:hover {
    background: transparent;
    color: #c4b2b2;
}

@media screen and (orientation: portrait) {

    #container {
    background-image: linear-gradient(to bottom, rgba(0,0,0,0.6) 0%,     rgba(0,0,0,0.7) 50%, rgba(0,0,0,0.9) 100%), url();
    background-size:cover;
    background-attachment: fixed, fixed;
    background-position: center;
    position: relative;
    }

div#container-wrap {
background: url()
    }

    body{

    font-family:"Noto Serif SC";
    color:#dedcdc;
    font-size:20px;
    letter-spacing:2px;
    }
}

#breadcrumbs {
    margin: 1em 0 1em;
}

