    border-radius: 0.25em;
    overflow: hidden;
    width: 60% ;
margin: 0 auto;
}

.pre-bankner span  {
font-size: 80% !important;
}

.pre-bankner {
    -ms-flex-preferred-size: calc(100% / 5);
        flex-basis: calc(100% / 5);
    color: rgb(var(--swatch-menutxt-light-color));
    padding: 1em;
    margin: 0.5em;
    border-radius: 0.25em;
    -webkit-box-flex: 2;
        -ms-flex-positive: 2;
            flex-grow: 2;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    flex-direction: column;
    position: relative;
    -webkit-box-pack: center;
        -ms-flex-pack: center;
            justify-content: center;
    -webkit-box-align: center;
        -ms-flex-align: center;
            align-items: center;
    text-align: center;
    border: 0.125rem solid rgb(var(--swatch-menutxt-light-color));
    background: rgb(var(--swatch-menubg-dark-color));
    font-size: calc(var(--base-font-size) * 1.25);
    overflow: hidden;
}

.pre-bankner span.page_rating {
    position: absolute;
    display: flex;
    bottom: -0.0625rem;
    right: -0.0625rem;
    min-width: 2em;
    min-height: 2em;
    text-align: center;
    width: auto !important;
    height: auto !important;
    border-left: 0.0625rem solid;
    border-top: 0.0625rem solid;
    border-radius: 0.25em 0.5em 0.25em 0.25em;
    padding: 0.25em;
    font-size: 60%;
}

.pre-bankner::before {
    content: " ";
    width: 200%;
    height: 25rem;
    position: absolute;
    top: -2rem;
    background-color: rgb(255, 219, 90);
    background-color: rgb(var(--bright-accent));
    -ms-transform: skew(30deg, 0);
    -o-transform: skew(30deg, 0);
    transform: skew(30deg, 0);
    right: 200%;
    will-change: right;
    -webkit-transition: right 500ms cubic-bezier(0.4, 0, 0.2, 1);
    -o-transition: right 500ms cubic-bezier(0.4, 0, 0.2, 1);
    transition: right 500ms cubic-bezier(0.4, 0, 0.2, 1);
    pointer-events: none;
}

.pre-bankner:hover::before {
    right: -6rem;
}

.pre-bankner a,
.pre-bankner   span {
    will-change: color, border, filter;
    -webkit-transition: color 500ms cubic-bezier(0.4, 0, 0.2, 1), border 500ms cubic-bezier(0.4, 0, 0.2, 1), border-top 500ms cubic-bezier(0.4, 0, 0.2, 1), filter 500ms cubic-bezier(0.4, 0, 0.2, 1);
    -o-transition: color 500ms cubic-bezier(0.4, 0, 0.2, 1), border 500ms cubic-bezier(0.4, 0, 0.2, 1), border-top 500ms cubic-bezier(0.4, 0, 0.2, 1), filter 500ms cubic-bezier(0.4, 0, 0.2, 1);
    transition: color 500ms cubic-bezier(0.4, 0, 0.2, 1), border 500ms cubic-bezier(0.4, 0, 0.2, 1), border-top 500ms cubic-bezier(0.4, 0, 0.2, 1), filter 500ms cubic-bezier(0.4, 0, 0.2, 1);
    z-index: 0;
    width: 100%;
    height: 0%;
    display: flex;
    align-items: center;
    justify-content: center;
    filter: invert(0) hue-rotate(0deg);
}

.pre-bankner a {
    border-top: 0.0625rem solid rgb(var(--swatch-menutxt-light-color));
    border-bottom: 0.0625rem solid rgb(var(--swatch-menutxt-light-color));
 padding: 0.5rem 0;
    font-size:120%;
}

.pre-bankner:hover a {
    color: #fff !important;
}

.pre-bankner:hover     span.page_rating {
    color: rgb(var(--swatch-menutxt-dark-color)) !important;  
}

.pre-bankner:hover   span.page_tags {
    color: rgb(var(--swatch-menutxt-dark-color)) !important;
 position: relative;
}

.pre-bankner:hover a {
    filter: invert(1) hue-rotate(195deg);
}

.pre-bankner   span {
    width: inherit;
    height: inherit;
    display:    inline;
}

.pre-bankner   sup {
    width: 0;
    height: 0;
    opacity: 0;
    font-size: 0;
}

@media only screen and (max-width: 768px) {
