[[module CSS]]
:root:lang(cn) {
    /* If you're making a new CSS theme, please include the following three variables at minimum. */
    --theme-base: "nuscp";
    /* must be either "nuscp" or "sigma9" */
    --theme-id: "dreamers-theme";
    /* set this to the URL of your theme's page - eg for "component:ar-theme", set it to "ar-theme" */
    --theme-name: "Dreamers' Library Theme";
    /* set this to your theme's full name */
    --header-title: "肉俗";
    --header-font: 'Playfair Display SC','Noto Serif SC', serif;
    --title-font: 'Playfair Display SC','Noto Serif SC', serif;
    --gradient-header: url("https://evernight-aquarium.wdfiles.com/local--files/files/006Vtzrtly1h5jazhuw6zj33uw06o45l.jpg");
    --logo-image: url("http://wanderers-library.wikidot.com/local--files/component:dreamers-theme/Oma-logo-22042013_300pp.png");
    /* ===STANDARD THEME COLORS=== */
    --white-monochrome: 252, 252, 252;
    --pale-gray-monochrome: 161, 161, 161;
    --light-pale-gray-monochrome: 255, 250, 215;
    --very-light-gray-monochrome: 220, 230, 220;
    --light-gray-monochrome: 130, 160, 135;
    --gray-monochrome: 79, 79, 79;
    --dark-gray-monochrome: 82, 82, 82;
    --black-monochrome: 250, 250, 250;
    --bright-accent: 235, 235, 235;
    --medium-accent: 163, 162, 160;
    --dark-accent: var(--dark-gray-monochrome);
    --alt-accent: 166, 157, 151;
    /* ===PRIMARY COLORS=== */
    /* ===GENERAL COLORS=== */
    --swatch-primary: var(--bright-accent);
    --swatch-primary-darker: var(--medium-accent);
    --swatch-primary-darkest: var(--dark-accent);
    --swatch-border-color: var(--medium-accent);
    /* ===GENERAL TEXT COLORS=== */
    --swatch-text-dark: var(--black-monochrome);
    --swatch-text-light: var(--white-monochrome);
    --swatch-important-text: var(--bright-accent);
    --newpage-color: 200, 200, 200;
    --visited-link-color: 120, 120, 120;
    /* ===BACKGROUND & HEADER COLORS=== */
    /* General Background Color */
    --swatch-background: 161, 161, 161, 1.0;
    --swatch-menubg-medium-color: var(--white-monochrome);
    --modal-body-header-txt: 0, 0, 0;
    --modal-body-text: 255, 255, 255;
    --tabs-content-bg-color: var(--swatch-secondary-color),1.0;
    /* Header H1 and H2 colors */
    --swatch-headerh1-color: var(--white-monochrome);
    --swatch-headerh2-color: var(--white-monochrome);
    /* ===MENU COLORS=== */
    /* ===MENU BACKGROUND COLORS=== */
    --swatch-menubg-color: var(--dark-gray-monochrome);
    /* ===MENU TEXT COLORS=== */
    --swatch-menutxt-dark-color: var(--dark-gray-monochrome);
    --swatch-menutxt-light-color: var(--bright-accent);
    /* ===TOPBAR MENU BACKGROUND COLORS=== */
    /* Colors used in the --gradient-topmenu var to give the topbar its color */
    --swatch-topmenu-bg-color: var(--dark-gray-monochrome);
    --swatch-topmenu-border-color: var(--bright-accent);
    /* ===LINK COLORS=== */
    --link-color: var(--medium-accent);
    --hover-link-color: var(--dark-gray-monochrome);
    --rating-module-button-color: var(--bright-accent);
    --rating-module-text-color: var(--bright-accent);
    /* ===RATING MODULE COLORS=== */
}
[[/module]]

