[[module CSS]]
:root {
/* By UncertaintyCrossing, based on the Wanderer's Depth theme by ROUNDERHOUSE,  which is based off Dustjacket by Woedenaz and Croquembouche */
    /* S-CSS-P Integration */
    /* If you're making a new CSS theme, please include the following three variables at minimum. */
    --theme-base: "nuscp";
    /* must be either "nuscp" or "sigma9" */
    --theme-id: "Cylus";
    /* set this to the URL of your theme's page - eg for "component:ar-theme", set it to "ar-theme" */
    --theme-name: "Cylus";
    /* set this to your theme's full name */

    /* Header */
    --logo-image: url("https://wanderers-library.wdfiles.com/local--files/the-gatekeeper-s-room/fancy%20eye.png");
    --header-title: "欢迎来到Cylus";
    --header-subtitle: " ";


    /* Typefaces */
    --header-font: 'Special Elite', serif;
    --title-font: 'Special Elite', serif;
    --body-font: 'Oswald', serif;
    --mono-font: "PT Mono", "Andale Mono", "Courier New", Courier, monospace;

    /* Standard Colors */
    --white-monochrome: 200, 161, 142;
    /* white */
    --pale-gray-monochrome: 244, 244, 244;
    /* v light gray for blockquotes and stuff */
    --light-gray-monochrome: 170, 170, 170;
    /* light accent gray for login status */
    --gray-monochrome: 66, 66, 72;
    /* gray */
    --dark-gray-monochrome: 48, 48, 52;
    /* dark accent gray for sidebar background */
    --black-monochrome: 12, 12, 12;
    /* black */
    --bright-accent: 226, 62, 16;
    /* bright ORANGE */
    --bright-two: 156, 127, 119;
    /* bright GREY */
    --medium-accent: 124, 50, 30;
    /*  RUST */
    --dark-accent: 61, 14, 1;
    /* dark RUST */
    --newpage-color: 221, 102, 17;
    /* pale orange */

    /* Primary Theme Colors */
    --tables-body-txt: var(--bright-accent);
    --swatch-background: var(--white-monochrome);
    --swatch-primary: var(--medium-accent);
    --swatch-primary-darker: var(--bright-accent);
    --swatch-primary-darkest: var(--bright-accent);
    /* Primary Text Colors */
    --swatch-text-dark: var(--black-monochrome);
    --swatch-text-light: var(--white-monochrome);
    --swatch-important-text: var(--bright-accent);
    --swatch-background: var(--mid-black);  
    --swatch-menubg-color: var(--gray-monochrome);
    --swatch-menubg-light-color: var(--pale-gray-monochrome);
    --swatch-menubg-medium-color: var(--light-gray-monochrome);
    --swatch-menubg-medium-dark-color: var(--black-monochrome);
    --swatch-menubg-dark-color: var(--dark-gray-monochrome);
    --swatch-menubg-black-color: var(--black-monochrome);
    --swatch-menubg-hover-color: var(--gray-monochrome);
    --swatch-menutxt-dark-color: var(--black-monochrome);
    --swatch-menutxt-light-color: var(--white-monochrome);
    --swatch-border-color: var(--black-monochrome);


    /* Primary Header Colors */
    --swatch-headerh1-color: var(--white-monochrome);
    --swatch-headerh2-color: var(--white-monochrome);
    --swatch-topmenu-border-color: var(--bright-accent);
    --swatch-topmenu-bg-color: var(--medium-accent);

    --rating-module-button-color: var(--bright-accent);
    --rating-module-text-color: var(--bright-accent);

    /* Interwiki */
    --head: var(--medium-accent);
    --link: var(--bright-two);

}
/* Rating Module */
.page-rate-widget-box {
    background: -webkit-gradient(linear,
        left bottom, left top,
        color-stop(0, rgba(61, 14, 1, 1)),
        to(rgba(124, 50, 30, 1)));
    background: -webkit-linear-gradient(bottom,
        rgba(61, 14, 1, 1) 0,
        rgba(124, 50, 30, 1) 100%);
    background: -moz-linear-gradient(bottom,
        rgba(61, 14, 1, 1) 0,
        rgba(124, 50, 30, 1) 100%);
    background: -o-linear-gradient(bottom,
        rgba(61, 14, 1, 1) 0,
        rgba(124, 50, 30, 1) 100%);
    background: linear-gradient(to top,
        rgba(61, 14, 1, 1) 0,
        rgba(124, 50, 30, 1) 100%);
    font-family: 'zuijinregular', 'Poppins', sans-serif;
    font-family: var(--header-font);
    letter-spacing: 0.05em;
}
#header h1 a::before {
    content: "Welcome to Cylus";
    content: var(--header-title);
    color: rgb(226, 62, 16);
    color: rgb(var(--bright-accent));
    text-shadow: inherit;
}

#header h1 a::after {
    background: url(https://wanderers-library.wdfiles.com/local--files/the-gatekeeper-s-room/header-texture.jpg);
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
}

#skrollr-body {
    width: 100%;
    height: 10rem;
    position: absolute;
    top: 0;
    left: 0;
    background-image: url(https://wanderers-library.wdfiles.com/local--files/the-gatekeeper-s-room/city-bg.png);
    background-size: cover;
    background-position: 50% 50%;
}

hr {
    height: .125rem;
    border: 0;
    border-top: 0.0625rem solid transparent;
    background-color: rgba(255, 219, 90);
    background-color: rgba(var(--bright-accent));
    background: url(https://wanderers-library.wdfiles.com/local--files/the-gatekeeper-s-room/header-texture.jpg);
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}

#container-wrap-wrap {
  width: 100%;
}

#content-wrap {
    background-image: url(https://wanderers-library.wdfiles.com/local--files/the-gatekeeper-s-room/wall-texture.jpg);
}

.page-rate-widget-box {
    border-radius: 0px 15px 0px 15px !important;
    background-color: #232d2c;
}

#top-bar div.top-bar > ul > li > ul {
    background: rgba(var(--medium-accent), 1);
}

#login-status, #my-account {
   color: rgb(var(--bright-accent)) !important;
}

#login-status a {
   color: rgb(var(--bright-accent)) !important;
}

#login-status > a > strong {
   color: rgb(var(--bright-accent)) !important;
}

#account-topbutton{
    border: solid 1px var(--bright-accent);
}
#account-options {
    width: auto;
    padding: 0.5em;
    border-color: rgba(var(--bright-accent));
    background: rgba(var(--medium-accent), 1);
    color: rgb(var(--swatch-text-light));
}

#top-bar div.top-bar > ul > li > ul {
    color: rgb(48, 48, 52);
}

#top-bar div.top-bar > ul > li > ul > a {
    color: rgb(115, 103, 89);
}

div#page-options-bottom > a,
div#page-options-bottom-2 > a {
    background: url(https://wanderers-library.wdfiles.com/local--files/the-gatekeeper-s-room/wall-texture.jpg);
    border: 0.15rem solid rgba(115, 103, 89), 1);
    color: rgba(115, 103, 89), 1);
}

div#page-options-bottom > a:hover,
div#page-options-bottom > a:active,
div#page-options-bottom-2 > a:hover,
div#page-options-bottom-2 > a:active {
    background-color: rgba(var(--swatch-menubg-light-color), 0,25);
    color: rgba(var(--bright-two), 1);
    border: 0.15rem solid rgba(var(--bright-two), 1);
}

a {
   color: #E23E10;
}

a:visited {
   color: #b60700;
}

/* Pop-Up Windows */
.owindow {
    background-color: #232d2c;
    border-color: var(--bright-accent);
}

.owindow .modal-header {
    background-color: #232d2c;
}

.owindow .modal-body img {
    background-color: transparent !important;
}

.owindow .title {
    background-color: #232d2c;
    color: #var(--bright-accent);
    border-bottom: 1px solid #2F333C;
}

.owindow .button-bar a {
    background-color: #232d2c;
    border-color: var(--bright-accent);
    color: #ededed;
}
.owindow .button-bar a:hover {
    background-color: var(--bright-accent);
}
/* mobile */

@media only screen and (max-width: 48rem) {


 div#extrac-div-3::before {
        text-shadow: inherit;
        background: url(https://wanderers-library.wdfiles.com/local--files/the-gatekeeper-s-room/header-texture.jpg);
        -webkit-background-clip: text;
        background-clip: text;
        -webkit-text-fill-color: transparent;
        content: "Ghost Record";
        content: var(--header-title);
        font-family: var(--header-font);
        font-size: 1%;
        width: 100%;
        height: 6 rem
        display: -webkit-box;
        display: -webkit-flex;
        display: -moz-box;
        display: -ms-flexbox;
        display: flex;
        -webkit-box-pack: center;
        -webkit-justify-content: center;
           -moz-box-pack: center;
            -ms-flex-pack: center;
                justify-content: center;
        -webkit-box-align: center;
        -webkit-align-items: center;
           -moz-box-align: center;
            -ms-flex-align: center;
                align-items: center;
        position: fixed;
        top: 0;
        right: 0;
        background-color: transparent;
        text-align: center;
        font-weight: 700;
    }


[[/module]]

