[[include :etinjat:typeface-cn
|I-PenCrane-B=true
]]
[[module CSS]]
/* 图书馆版式复位 */
#side-bar {
    background-color: rgba(var(--swatch-menubg-color),1) !important;
    box-shadow: inherit;
    border-radius: inherit;
}
#side-bar .side-block{
    background-color: rgba(0,0,0,0) !important;
}
#side-bar .heading {
    width: inherit;
    margin: inherit;
}
span:not([class]):not([style]) > sup.footnoteref:first-child > a.footnoteref:before,
sup.footnoteref > a.footnoteref:before {
content: inherit;
}
span:not([class]):not([style]) > sup.footnoteref > a.footnoteref:before {
content: inherit;
}
iframe.scpnet-interwiki-frame {
  filter: inherit;
  -webkit-filter: inherit;
}
[[/module]]

[[include :scp-wiki-cn:theme:black-highlighter-theme]]
[[include :scp-wiki-cn:theme:walk-in-the-dark]]


[[module CSS]]


.phone {
float:center;
height: auto; 
writing-mode: vertical-rl;
writing-mode: tb-lr;
font-size:300%;
font-family: 'I.PenCrane-B', 润植家康熙字典美化体, nzgrKangxi, 'Ma Shan Zheng', '华文隶书', lisu,  'Noto Serif SC' , '思源宋体 CN';
margin: 2em 2em 0 2em;
letter-spacing: 0.3em;
line-height: 1.5em;
}

@media (max-width: 479px) {
.phone {
  font-size:150% !important;
margin: 2em 1.5em 0 1.5em;
}

}

#page-title {
   display:none;
}

@media (min-width: 1000px) {

.rate-lr{
float:right;
transform:rotate(90deg);
-ms-transform:rotate(90deg);     /* IE 9 */
-moz-transform:rotate(90deg);     /* Firefox */
-webkit-transform:rotate(90deg); /* Safari 和 Chrome */
-o-transform:rotate(90deg);     
}

.rate-points:not(.number){
writing-mode: sideways-lr;
padding: 0.5em ;
  margin: 0 2em 0em 0;
}

.rate-points .prw54353{
left:3em;
bottom: 0.1em;
  position: absolute;
  writing-mode:  horizontal-tb;

}

.page-rate-widget-box{
  width: 10em;
}

}


[[/module]]
