[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@300;400;700;900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif:wght@300;400;700;900&display=swap');

div#page-content {
    color: #000;
    font-size: 0.9375rem;
    font-family: "Noto Serif", "Noto Serif SC", serif;
}

@media (max-width: 767px) {
div#page-content {
    font-size: 0.8125rem;
}
}

div#page-content h1,
div#page-content h2,
div#page-content h3,
div#page-content h4,
div#page-content h5,
div#page-content h6 {
    font-family: "Noto Serif", "Noto Serif SC", serif;
    font-weight: 700;
}

[[/module]]

[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@400;700;900');

#page-title {
    color: #000;
    font-size: 2rem;
    font-family: "Noto Serif SC", serif;
    font-weight: 900;
}
[[/module]]


