content:
[[module css]]
#page-title {
    display: none;
}

div.inner {
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
}
div.inner > div {
    -webkit-box-flex: 2;
    -webkit-flex-grow: 2;
       -moz-box-flex: 2;
        -ms-flex-positive: 2;
            flex-grow: 2;
    -webkit-flex-basis: -webkit-calc(50% - 2em);
        -ms-flex-preferred-size: calc(50% - 2em);
            flex-basis: calc(50% - 2em);
    padding: 1em;
}

@media only screen and (max-width: 768px) {
    div.inner {
        -webkit-flex-wrap: wrap;
            -ms-flex-wrap: wrap;
                flex-wrap: wrap;
    }
    div.inner > div {
        -webkit-flex-basis: 100%;
            -ms-flex-preferred-size: 100%;
                flex-basis: 100%;
    }
}

div.left-side, div.right-side {
    font-size: 120%;
}
div.right-side {
    border-left: 0.25rem double rgb(255, 219, 90);
}

span.h2 {
    font-weight: 600;
    font-size: 175%;
    letter-spacing: 0.1em;
}
span.h3 {
    font-weight: 900;
    font-size: 130%;
    letter-spacing: 0.063rem;
}
[[/module]]

