[[module CSS]]
@import url(http://scp-wiki-cn.wikidot.com/component:theme/code/1);

@media (max-width: 479px) {
#header h1{
    margin-right: 0px;
    font-size: 70%;
}

#header h2 span  {
    margin-right: 0px;
    font-size: 50%;
}

#search-top-box {
display:none
}
}

#side-bar {
background-color: transparent !important;
}

#side-bar .side-block {
    background-color: transparent !important;
}


div#container-wrap {
    background: url(http://scp-wiki-cn.wikidot.com/local--files/wanderers:the-ballad-that-sunset/Plukje-s-tree-189852_1280.jpg) top left no-repeat;
    background-size: 100% 100%;
}

#top-bar a , #login-status{
    color: #833838;
}

a.footnoteref {
    display: inline-block;
    font-size: 60%;
}

#header  {
    background-image: url(http://scpsandboxcn.wdfiles.com/local--files/etinjat-2/Liber.jpg);
    background-repeat: no-repeat;
    background-size: 90px 90px;
}

#header h1 {
    padding: 0;
    float: left;
    max-height: 90px;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 75px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-size: 140%;
    font-family:"华文新魏";
    text-shadow: none;
    text-decoration: none;
}

#header h1 a::before {
    content: "被放鸽者之图书馆";
    color: #FFFFFF;
    text-shadow: 3px 3px 5px #000000;
}

#header h2 span {
    display: block;
    margin: 0;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    text-shadow: none;
}

#header h2 {
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}


#header h2 span::before {
    content: "一日不见，如隔三秋";
    display: block;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color:#f9c7a0;
    text-shadow: 1px 1px 1px #000000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
    font-size: 110%;
    font-family: Kaiti,SimSun,sans-serif;
    padding: 5px 0;
    margin: 0;
    text-indent: 15px;
}

#login-status {
    font-size: 100%;
    font-family: STXingkai;Kaiti,SimSun,sans-serif,etinjat;
}

#page-title {
    font-family:'华文隶书',STXingkai,Kaiti,SimSun,sans-serif,etinjat;
font-size: 300%;
    text-align: center;
}
[[/module]]
