[[module css]]
div.bookshelf .yui-navset .yui-nav {
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-flex-wrap: wrap;
        -ms-flex-wrap: wrap;
            flex-wrap: wrap;
    width: 100%;
    border-style: none;
}

div.bookshelf .yui-navset .yui-nav li {
    -ms-flex-preferred-size: calc(100% / 8);
        -webkit-flex-basis: -webkit-calc(100% / 8);
            flex-basis: -moz-calc(100% / 8);
            flex-basis: calc(100% / 8);
    -webkit-box-flex: 2;
    -webkit-flex-grow: 2;
       -moz-box-flex: 2;
        -ms-flex-positive: 2;
            flex-grow: 2;
    margin: 0;
    padding: 0.25em 0.5em;
}

div.bookshelf .yui-navset .yui-nav li a {
    display: block;
    background-color: rgb(30, 55, 30);
    background-image: none;
    border: 0.125rem solid rgb(255, 219, 90);
    -webkit-border-radius: 0.25em;
       -moz-border-radius: 0.25em;
            border-radius: 0.25em;
    color: rgb(255, 219, 90);
    padding: 0.5em;
    text-align: center;
}

div.bookshelf .yui-navset .yui-nav li a:hover,
div.bookshelf .yui-navset .yui-nav li a:focus {
    color: rgb(252, 252, 252);
}

div.bookshelf .yui-navset .yui-nav li.selected a,
div.bookshelf .yui-navset .yui-nav li.selected a:focus,
div.bookshelf .yui-navset .yui-nav li.selected a:hover {
    background-color: rgb(30, 55, 30);
    background-image: none;
    color: rgb(255, 219, 90);
}

div.bookshelf .yui-navset .yui-nav li a em {
    position: initial;
    padding: 0;
    border-style: none;
    font-size: 1.25em;
    white-space: nowrap;
}

div.bookshelf .yui-navset .yui-nav li a em::before,
div.bookshelf .yui-navset .yui-nav li a em::after {
    padding: 0 0.5em;
    color: transparent;
}

div.bookshelf .yui-navset .yui-nav li a em::before {
    content: "↬";
}

div.bookshelf .yui-navset .yui-nav li a em::after {
    content: "↫";
}

div.bookshelf .yui-navset .yui-nav li a:focus em::before,
div.bookshelf .yui-navset .yui-nav li a:focus em::after,
div.bookshelf .yui-navset .yui-nav li a:hover em::before,
div.bookshelf .yui-navset .yui-nav li a:hover em::after {
    color: rgb(252, 252, 252);
}

div.bookshelf .yui-navset .yui-nav li.selected a em::before,
div.bookshelf .yui-navset .yui-nav li.selected a em::after {
    color: rgb(255, 219, 90);
}

div.bookshelf .yui-navset .yui-content {
    background-color: transparent;
    background-image: -webkit-gradient(linear, left top, left bottom, from(rgb(30, 55, 30)), color-stop(30%, rgb(30, 55, 30)), to(rgb(255, 219, 90)));
    background-image: -webkit-linear-gradient(rgb(30, 55, 30) 0%, rgb(30, 55, 30) 30%, rgb(255, 219, 90) 100%);
    background-image: -moz-linear-gradient(rgb(30, 55, 30) 0%, rgb(30, 55, 30) 30%, rgb(255, 219, 90) 100%);
    background-image: -o-linear-gradient(rgb(30, 55, 30) 0%, rgb(30, 55, 30) 30%, rgb(255, 219, 90) 100%);
    background-image: linear-gradient(rgb(30, 55, 30) 0%, rgb(30, 55, 30) 30%, rgb(255, 219, 90) 100%);
    -webkit-background-size: 100% 200%;
       -moz-background-size: 100% 200%;
         -o-background-size: 100% 200%;
            background-size: 100% 200%;
    border-style: none;
    -webkit-border-radius: 0.5em;
       -moz-border-radius: 0.5em;
            border-radius: 0.5em;
    padding: 2em;
    position: relative;
    overflow: hidden;
    height: 100%;
}

@supports (background-blend-mode: screen) {
    div.bookshelf .yui-navset .yui-content {
        background-image: url(http://wanderers-library.wikidot.com/local--files/component:theme/goldfoil.png), -webkit-gradient(linear, left top, left bottom, from(rgb(30, 55, 30)), color-stop(30%, rgb(30, 55, 30)), to(rgb(255, 219, 90)));
        background-image: url(http://wanderers-library.wikidot.com/local--files/component:theme/goldfoil.png), -webkit-linear-gradient(rgb(30, 55, 30) 0%, rgb(30, 55, 30) 30%, rgb(255, 219, 90) 100%);
        background-image: url(http://wanderers-library.wikidot.com/local--files/component:theme/goldfoil.png), -moz-linear-gradient(rgb(30, 55, 30) 0%, rgb(30, 55, 30) 30%, rgb(255, 219, 90) 100%);
        background-image: url(http://wanderers-library.wikidot.com/local--files/component:theme/goldfoil.png), -o-linear-gradient(rgb(30, 55, 30) 0%, rgb(30, 55, 30) 30%, rgb(255, 219, 90) 100%);
        background-image: url(http://wanderers-library.wikidot.com/local--files/component:theme/goldfoil.png), linear-gradient(rgb(30, 55, 30) 0%, rgb(30, 55, 30) 30%, rgb(255, 219, 90) 100%);
        background-blend-mode: overlay, normal;
        -webkit-background-size: cover, 100% 200%;
           -moz-background-size: cover, 100% 200%;
             -o-background-size: cover, 100% 200%;
                background-size: cover, 100% 200%;
    }
}

div.bookshelf .yui-navset .yui-content::before {
    content: " ";
    background-image: -webkit-gradient(linear, left top, left bottom, from(rgb(30, 55, 30)), color-stop(30%, rgb(30, 55, 30)), to(rgb(255, 219, 90)));
    background-image: -webkit-linear-gradient(rgb(30, 55, 30) 0%, rgb(30, 55, 30) 30%, rgb(255, 219, 90) 100%);
    background-image: -moz-linear-gradient(rgb(30, 55, 30) 0%, rgb(30, 55, 30) 30%, rgb(255, 219, 90) 100%);
    background-image: -o-linear-gradient(rgb(30, 55, 30) 0%, rgb(30, 55, 30) 30%, rgb(255, 219, 90) 100%);
    background-image: linear-gradient(rgb(30, 55, 30) 0%, rgb(30, 55, 30) 30%, rgb(255, 219, 90) 100%);
    -webkit-background-size: 100% 200%;
       -moz-background-size: 100% 200%;
         -o-background-size: 100% 200%;
            background-size: 100% 200%;
    width: 100%;
    height: 100%;
    position: absolute;
    z-index: 2;
    top: 0;
    left: 0;
    -webkit-border-radius: 0.5em;
       -moz-border-radius: 0.5em;
            border-radius: 0.5em;
    -webkit-transform: rotateX(180deg);
       -moz-transform: rotateX(180deg);
            transform: rotateX(180deg);
    mix-blend-mode: overlay;
    opacity: 0.25;
    pointer-events: none;
}

div.bookshelf div.list-pages-box {
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-flex-wrap: wrap;
        -ms-flex-wrap: wrap;
            flex-wrap: wrap;
    -webkit-box-align: center;
    -webkit-align-items: center;
       -moz-box-align: center;
        -ms-flex-align: center;
            align-items: center;
    -webkit-align-content: flex-end;
        -ms-flex-line-pack: end;
            align-content: flex-end;
    -webkit-transform: perspective(500rem);
       -moz-transform: perspective(500rem);
            transform: perspective(500rem);
    -webkit-transform-style: preserve-3d;
       -moz-transform-style: preserve-3d;
            transform-style: preserve-3d;
    -webkit-transform-origin: top center;
       -moz-transform-origin: top center;
        -ms-transform-origin: top center;
         -o-transform-origin: top center;
            transform-origin: top center;
    position: relative;
}

div.bookshelf div.list-pages-box:last-child {
    margin-bottom: 2em;
}

div.bookshelf div.list-pages-box:last-child::after,
div.bookshelf div.list-pages-box:last-child::before {
    content: " ";
    position: absolute;
    width: 100%;
    height: 1em;
}

div.bookshelf div.list-pages-box:last-child::after {
    background-color: rgb(185, 150, 17);
    -webkit-transform: translateZ(2em) translateY(0.1em);
       -moz-transform: translateZ(2em) translateY(0.1em);
            transform: translateZ(2em) translateY(0.1em);
    bottom: -1em;
    -webkit-border-radius: 0 0 0.25rem 0.25rem;
       -moz-border-radius: 0 0 0.25rem 0.25rem;
            border-radius: 0 0 0.25rem 0.25rem;
}

@supports (background-blend-mode: screen) {
    div.bookshelf div.list-pages-box:last-child::after {
        background-image: url(http://wanderers-library.wikidot.com/local--files/component:theme/goldfoil.png);
        background-blend-mode: overlay;
    }
}

div.bookshelf div.list-pages-box:last-child::before {
    bottom: -1.2em;
    background-color: rgb(30, 55, 30);
    -webkit-transform: rotateX(92deg) scaleY(35) translateZ(-0.5em) translateY(-1em);
       -moz-transform: rotateX(92deg) scaleY(35) translateZ(-0.5em) translateY(-1em);
            transform: rotateX(92deg) scaleY(35) translateZ(-0.5em) translateY(-1em);
    margin: 0 auto;
    width: 104%;
    left: -2%;
}

div.bookshelf .book {
    width: 95%;
    height: auto;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    position: relative;
    margin: 0.05em auto;
    padding: 0.25rem;
    overflow: hidden;
    -webkit-border-radius: 0.5em;
       -moz-border-radius: 0.5em;
            border-radius: 0.5em;
    -webkit-transform-style: preserve-3d;
       -moz-transform-style: preserve-3d;
            transform-style: preserve-3d;
    -webkit-transition: -webkit-transform 500ms cubic-bezier(0.4, 0, 0.2, 1);
    transition: -webkit-transform 500ms cubic-bezier(0.4, 0, 0.2, 1);
    -o-transition: -o-transform 500ms cubic-bezier(0.4, 0, 0.2, 1);
    -moz-transition: transform 500ms cubic-bezier(0.4, 0, 0.2, 1), -moz-transform 500ms cubic-bezier(0.4, 0, 0.2, 1);
    transition: transform 500ms cubic-bezier(0.4, 0, 0.2, 1);
    transition: transform 500ms cubic-bezier(0.4, 0, 0.2, 1), -webkit-transform 500ms cubic-bezier(0.4, 0, 0.2, 1), -moz-transform 500ms cubic-bezier(0.4, 0, 0.2, 1), -o-transform 500ms cubic-bezier(0.4, 0, 0.2, 1);
    -webkit-box-shadow: 0.688rem 0.375rem 0.875rem rgba(30, 55, 30, 0.25), 0.188rem 0px 0.313rem rgba(30, 55, 30, 0.22);
       -moz-box-shadow: 0.688rem 0.375rem 0.875rem rgba(30, 55, 30, 0.25), 0.188rem 0px 0.313rem rgba(30, 55, 30, 0.22);
            box-shadow: 0.688rem 0.375rem 0.875rem rgba(30, 55, 30, 0.25), 0.188rem 0px 0.313rem rgba(30, 55, 30, 0.22);
}

div.bookshelf .book:hover {
    -webkit-transform: translateZ(10rem) translateY(-0.35em) rotateY(5deg);
       -moz-transform: translateZ(10rem) translateY(-0.35em) rotateY(5deg);
            transform: translateZ(10rem) translateY(-0.35em) rotateY(5deg);
}

div.bookshelf .book,
div.bookshelf .book:hover,
div.bookshelf .book:active,
div.bookshelf .book:visited {
    text-decoration: none;
    color: rgb(255, 219, 90);
}

div.bookshelf .book::before {
    content: " ";
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0.25rem;
    left: 0;
    background-image: -webkit-gradient(linear, left top, left bottom, from(rgba(178, 210, 180)),color-stop(4%, rgba(45, 70, 45)),color-stop(4%, rgba(45, 70, 45)),color-stop(95%, rgba(45, 70, 45)),to(rgba(30, 55, 30)));
    background-image: -webkit-linear-gradient(top, rgba(178, 210, 180) 0%,rgba(45, 70, 45) 4%,rgba(45, 70, 45) 4%,rgba(45, 70, 45) 95%,rgba(30, 55, 30) 100%);
    background-image: -moz-linear-gradient(top, rgba(178, 210, 180) 0%,rgba(45, 70, 45) 4%,rgba(45, 70, 45) 4%,rgba(45, 70, 45) 95%,rgba(30, 55, 30) 100%);
    background-image: -o-linear-gradient(top, rgba(178, 210, 180) 0%,rgba(45, 70, 45) 4%,rgba(45, 70, 45) 4%,rgba(45, 70, 45) 95%,rgba(30, 55, 30) 100%);
    background-image: linear-gradient(to bottom, rgba(178, 210, 180) 0%,rgba(45, 70, 45) 4%,rgba(45, 70, 45) 4%,rgba(45, 70, 45) 95%,rgba(30, 55, 30) 100%);
    z-index: -1;
    pointer-events: none;
}

div.bookshelf .book::after {
    content: " ";
    width: 100%;
    height: 100%;
    position: absolute;
    top: -0.125rem;
    left: 0;
    background-image: -webkit-linear-gradient(270.5deg, rgba(252, 252, 252) 0%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 97%,rgba(12, 12, 12) 100%);
    background-image: -moz-linear-gradient(270.5deg, rgba(252, 252, 252) 0%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 97%,rgba(12, 12, 12) 100%);
    background-image: -o-linear-gradient(270.5deg, rgba(252, 252, 252) 0%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 97%,rgba(12, 12, 12) 100%);
    background-image: linear-gradient(179.5deg, rgba(252, 252, 252) 0%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 97%,rgba(12, 12, 12) 100%);
    z-index: -1;
    -webkit-background-size: 100% 100%;
       -moz-background-size: 100% 100%;
         -o-background-size: 100% 100%;
            background-size: 100% 100%;
    pointer-events: none;
}

@supports (background-blend-mode: screen) {
    div.bookshelf .book::after {
        background-image: -webkit-linear-gradient(270.5deg, rgba(252, 252, 252) 0%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 97%,rgba(12, 12, 12) 100%), url(http://wanderers-library.wikidot.com/local--files/component:theme/wlheader_bg.png);
        background-image: -moz-linear-gradient(270.5deg, rgba(252, 252, 252) 0%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 97%,rgba(12, 12, 12) 100%), url(http://wanderers-library.wikidot.com/local--files/component:theme/wlheader_bg.png);
        background-image: -o-linear-gradient(270.5deg, rgba(252, 252, 252) 0%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 97%,rgba(12, 12, 12) 100%), url(http://wanderers-library.wikidot.com/local--files/component:theme/wlheader_bg.png);
        background-image: linear-gradient(179.5deg, rgba(252, 252, 252) 0%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 10%,rgba(45, 70, 45) 97%,rgba(12, 12, 12) 100%), url(http://wanderers-library.wikidot.com/local--files/component:theme/wlheader_bg.png);
        mix-blend-mode: screen;
        background-blend-mode: color-dodge;
        -webkit-background-size: 100% 100%, contain;
           -moz-background-size: 100% 100%, contain;
             -o-background-size: 100% 100%, contain;
                background-size: 100% 100%, contain;
    }
}

div.bookshelf .book > * {
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -webkit-align-items: center;
       -moz-box-align: center;
        -ms-flex-align: center;
            align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
       -moz-box-pack: center;
        -ms-flex-pack: center;
            justify-content: center;
    padding: 0.25rem;
}

div.bookshelf .book .title {
    -webkit-flex-wrap: wrap;
        -ms-flex-wrap: wrap;
            flex-wrap: wrap;
    font-weight: 900;
    border-left: 0.25rem double rgb(228, 180, 28);
}

div.bookshelf .book .title > * {
    display: block;
    width: 100%;
}

div.bookshelf .book .title > *:first-child {
    font-size: 0.6em;
    text-transform: capitalize;
}

div.bookshelf .book .author {
    -webkit-box-flex: 2;
    -webkit-flex-grow: 2;
       -moz-box-flex: 2;
        -ms-flex-positive: 2;
            flex-grow: 2;
    color: rgb(178, 210, 180);
    font-weight: 700;
    font-size: 0.75em;
    border-right: 0.25rem double rgb(228, 180, 28);
    text-align: right;
    -webkit-box-pack: end;
    -webkit-justify-content: flex-end;
       -moz-box-pack: end;
        -ms-flex-pack: end;
            justify-content: flex-end;
    padding-right: 1em;
}

div.bookshelf .book .author * {
    word-break: keep-all;
}

div.bookshelf .book .author .user-deleted {
    display: none;
}
[[/module]]

