[[include :scp-wiki-cn:theme:dustjacket-sigma]]
[[module css]]
@import url('https://fonts.googleapis.com/css?family=Noto+Sans+SC|Noto+Serif+SC|Noto+Sans+TC|Noto+Serif+TC&display=swap');

body {
    font-family: 'Noto Serif SC',verdana,arial,helvetica,sans-serif;
}
textarea[name=source] {
    font-family: 'Noto Serif SC', 'Noto Serif TC';
}
#page-title {
    text-align: center;
}
#page-title { display: none; }
[[/module]]
[[module CSS]]
.disappearance {
   animation: hide6 10s infinite alternate forwards;
}
@keyframes hide6 {
  0% {
    opacity: 0.6;
    filter: blur(1px);
  }
  50% {
    opacity: 0.7;
    filter: blur(2px);
  }
  100% {
    opacity: 0.8;
    filter: blur(3px);
  }
}
.disappearance1 {
   animation: hide1 10s infinite alternate forwards;
}
@keyframes hide1 {
  0% {
    opacity: 0.6;
    filter: blur(0.5px);
  }
  50% {
    opacity: 0.7;
    filter: blur(1px);
  }
  100% {
    opacity: 0.8;
    filter: blur(1.5px);
  }
}

.disappearance2 {
   animation: hide2 10s infinite alternate forwards;
}
@keyframes hide2 {
  0% {
    opacity: 0.6;
    filter: blur(0px);
  }
  50% {
    opacity: 0.7;
    filter: blur(0.5px);
  }
  100% {
    opacity: 0.8;
    filter: blur(1px);
  }
}

.disappearance3 {
   animation: hide3 10s infinite alternate forwards;
}
@keyframes hide3 {
  0% {
    opacity: 0.6;
    filter: blur(0px);
  }
  50% {
    opacity: 0.7;
    filter: blur(0.25px);
  }
  100% {
    opacity: 0.8;
    filter: blur(0.5px);
  }
}
[[/module]]
