    -webkit-mask-image:
     -webkit-gradient(linear,
     left bottom, left top,
     from(rgba(0, 0, 0, 1)),
     color-stop(33%, rgba(0, 0, 0, 1)),
     color-stop(66%, rgba(0, 0, 0, 0)),
     to(rgba(0, 0, 0, 0)));
