.letter {
    font-family: 'Caveat';font-size: 17px;font-weight: 700;}

.lettera {
    font-family: 'Architects Daughter';font-size: 15px;font-weight: 700;}

.letterb {
    font-family: 'Satisfy';font-size: 15px;font-weight: 700;}

.letterc {
    font-family: 'Miniver';font-size: 15px;font-weight: 700;}
h1 {
color: black;
font-family: 'Trebuchet MS';
}

.first {
width: 1000px;
height: 1000px;
overflow: hidden;
background-repeat: no-repeat;
background-position: center;
background-color: white;
background-image: url('http://scpsandbox2.wikidot.com/local--files/karnickel/treea.jpg');
margin: auto;
border-radius: 100px 100px 100px 100px;
}
.first .second{
position: relative;
width: 90%;
height: 90%;
margin: auto;
margin-top: 5%;
border-style: dashed;
border-width: 0px;
border-radius: 100px 100px 100px 100px;
padding: 10px;
font-family: 'Aclonica';
color: rgb(75,65,50);
text-shadow:
   -1px -1px 0 rgb(200,175,150),  
    1px -1px 0 rgb(200,175,150),
    -1px 1px 0 rgb(200,175,150),
     1px 1px 0 rgb(200,175,150);
font-size: 17px;font-weight: 800;
text-align: center;
}
.third{
width: 600px;
margin: auto;
border-style: dashed;
border-width: 1px;
border-radius: 50px 50px 50px 50px;
padding: 10px;
}
[[/module]]
