[[module css]]
.enlarge{
transition: .25s;
}

.enlarge:hover{
transform: scale(1.3);
transition: .25s;
transition-delay: .4s;
z-index: 1000;
}

.col-wrap {
  margin-bottom: 1em;
  overflow-x: hidden;
}
.col-wrap .collapsible-block {
  border-left: solid 0.5rem var(--bg-color);
}
.col-wrap > .collapsible-block,
.col-wrap > .collapsible-block > .collapsible-block-unfolded {
  display: -webkit-box !important;
  display: -webkit-flex !important;
  display: -moz-box !important;
  display: -ms-flexbox !important;
  display: flex !important;
}
.col-wrap > .collapsible-block > .collapsible-block-folded,
.col-wrap > .collapsible-block > .collapsible-block-unfolded,
.col-wrap > .collapsible-block > .collapsible-block-unfolded > .collapsible-block-unfolded-link,
.col-wrap > .collapsible-block > .collapsible-block-unfolded > .collapsible-block-content {
  -webkit-box-flex: 1;
  -webkit-flex-grow: 1;
  -moz-box-flex: 1;
  -ms-flex-positive: 1;
  flex-grow: 1;
  -webkit-flex-shrink: 0;
  -ms-flex-negative: 0;
  flex-shrink: 0;
  width: 100%;
}
.col-wrap .collapsible-block-unfolded[style*="none"] > .collapsible-block-unfolded-link {
  display: none;
}
.col-wrap a.collapsible-block-link {
  display: block;
  position: relative;
  height: 100%;
  padding-left: 0.4375rem;
  z-index: 1;
  font-size: 0;
}
.col-wrap > .collapsible-block > .collapsible-block-folded:hover,
.col-wrap > .collapsible-block > .collapsible-block-unfolded > .collapsible-block-unfolded-link:hover {
  background-color: #eee;
}
.col-wrap > .collapsible-block > .collapsible-block-unfolded > .collapsible-block-content {
  position: relative;
  left: -100%;
  padding-left: 0.4372rem;
  opacity: 1 !important;
}
.col-wrap > .collapsible-block > .collapsible-block-unfolded > .collapsible-block-content .collapsible-block-content {
  position: relative;
  z-index: 1;
}

/*
Simple Theme by 7happy7
http://topia.wikidot.com/component:simple
*/

.yui-navset {
    display: flex;
    flex-direction: row;
    margin-bottom: 1em;
    vertical-align: top;
    width: 100%;
}
.yui-navset .yui-nav li,
.yui-navset .yui-navset-top .yui-nav li {
    display: block;
    writing-mode: vertical-rl;
    width: 100%;
    margin: 0!important;
    padding: unset;
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background: #212121;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: #ddd;
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: #fff;
    background-image: none;
    border-width: 1px 0 1px 1px;
}
.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
    line-height: 141%;
    padding: 0.75em!important;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-bottom: none;
    border-color: #212121;
    display: block;
    width: 2.5em;
    border-right: solid #333 2px;
    padding: 0;
    margin: 0;
}
.yui-navset .yui-content {
    background-color: #fff;
    border: solid #333 1px;
    display: block;
    width: calc(100% - 2.5em);
}
.yui-navset .yui-content > div:before {
    content: "";
}

@import url('https://fonts.googleapis.com/css?family=Noto+Sans+TC|Noto+Serif+TC:700');
@import url(http://scp-sandbox-3.wikidot.com/local--code/component:todaysdate/7);
div#container-wrap {
    background: url(http://kaktuskontainer.wdfiles.com/local--files/stormbreath-hub-page/body_bg.png) top left repeat-x;}

/* 藍光字 */
.neon {
  font-family:'Noto Sans TC';
  color:#8ec3f9;
  text-shadow: 0 0 5px #f0f, 0 0 1px #00f, 0 0 1px #f00, 0 0 1px #00f,
    0 0 5px #0ff, 0 0 10px #0ff, 0 0 1px #f0f, 0 0 1px #fff;
}
/* 換左上標題 */
#header {background-image: url(http://scpsandboxcn.wdfiles.com/local--files/feitag/go.png);}
#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'Noto Serif TC', serif;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.9px;
}

#header h1 a::before {
    content: "西 部 梦 神";
    color: #fff;
    letter-spacing: -0.02em;
    text-shadow: 3px 3px 5px #000;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-family: 'Noto Sans TC';
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: '万 般 皆 为 幻 梦';
    color: #fff;
    font-style: italic;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

sup {
  vertical-align: top;
  position: relative;
  top: -0.5em;
}

/* 連結顏色與模糊特效 */
.menu a:link{color: #9900FF}
.menu a:visited{color: #9900FF}
.menu a:hover{color: #cd00bd}

.menub a:link{color: #2200AA}
.menub a:visited{color: #2200AA}
.menub a:hover{color: #cd00bd}


.underline-on-hover a:link{color: black}

.blur {color: transparent; text-shadow: 0 0 6px rgba(0,0,0,.3); }

/* 評分鈕 */
.page-rate-widget-box .rate-points {
  background-color: #61006c !important;
  border: solid 1px #61006c;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #f0f8ff;
  border-top: solid 1px #61006c;
  border-bottom: solid 1px #61006c;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  color: #006c00;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background: #61006c;
  color: #f0f8ff;
}
.page-rate-widget-box .cancel {
  background-color: #61006c;
  border: solid 1px #61006c;
}
.page-rate-widget-box .cancel a {
  color: #f0f8ff;
}
.page-rate-widget-box .cancel a:hover {
  background: #dccdea;
  color: #f0f8ff;
}
