    position: absolute;
    display: inline;
    margin: 0px 0px;
    height: auto;
    width: auto;
    background: #fff;
    border: 2px solid rgb(var(--medium-accent));
    color: rgb(var(--medium-accent));
    padding: 1em;
    z-index: 2;
}
