    -webkit-mask-image: linear-gradient(to bottom, black 0%, rgba(0,0,0,0.5) 60%, transparent 82%);
    mask-image: linear-gradient(to bottom, black 0%, rgba(0,0,0,0.5) 60%, transparent 82%);
    margin-bottom: -6em;
}

