[[module CSS]]
/*
    Powered on 2020/10/17 11:59:42
    [2020 Wikidot Theme]
    Created by Dr_Kudo
    CC BY-SA 3.0
*/

@import url('https://fonts.googleapis.com/css?family=Noto+Serif+JP&display=swap');

/*フォントやテーマ色です。特に弄らなくても大丈夫なはず*/
:root {
    --bg: #c0bf6b;
    --fg: #140101;
}
body {
    background: rgba(202, 204, 92, 0.829);
    color: rgb(0, 0, 0);
    font-family: 'Sawarabi Mincho', sans-serif;
}

a,a:visited {
color: rgb(0, 0, 0);
}

/*
リンクの色を変えます
*/

/*
div#container-wrap: ulrを指定してヘッダの背景を調整します
*/
div#container-wrap {
background: url(http://scp-jp-sandbox3.wikidot.com/local--files/portal:6198158/head_kai.png) top left repeat-x;
}

/*
ヘッダを弄ります。文字と間隔
*/

div#header h1 a span {
font-size: 0px;
}
div#header h1 a:before {
content: "광역괴이수용사례기록";
color: rgba(240,250,255,0.8);

}
div#header h2 span {
font-size:0px;
padding: 4px;
}
div#header h2:after {
content: "수집・조사・기도";
font-weight: bold;
color: rgba(240,250,255,0.8);
padding: 60px 0;
white-space: pre;
}

/*
サイドバー：よく分かりません……。ひとまず書いてみて考えます
*/

#side-bar .side-block:nth-child(1),
#side-bar .side-block:nth-child(5),
#side-bar .side-block:nth-child(6),
#side-bar .side-block:nth-child(7) {
    background-color: rgba(240,250,255,0.8) !important;
}
div#side-bar div.side-block {
    background: rgba(240,250,255,0.8);
    border: 1px solid #000000;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 5);
}
div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: rgb(0, 0, 0);
    border-color: rgb(0, 0, 0);
}
@media (max-width: 767px) {
    #side-bar{
        background-color: transparent;
    }
}

/*
タブ ボタンの色とか弄れます
*/

div.yui-navset div.yui-content {
background: rgba(240,250,255,0.8);
}
div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
background: #d8d8d8;
color: #000000;
border: solid #a3a3a3;
}
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
background: #fca6a6;
color: #000000;
}
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
background: rgb(218, 215, 142);
color: #000000;
border: sokid #243356;
}
div.open-menu a {
color: #888888!important;
border-color: #888888!important;
}

/*
rate-boxです
*/

div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: rgba(90, 47, 62, 0.726)!important;
    border: solid rgba(90, 47, 62, 0.726) 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid rgba(90, 47, 62, 0.726)!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: rgb(255, 255, 255);
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: rgb(255, 255, 255)!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color:rgba(90, 47, 62, 0.726)!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: transparent!important;
}

.modalbox .credit {
    color: rgb(0, 0, 0)
}
[[/module]]

