[[module css]]
.extra-side-bar {
	float: right; 
	width: 15em; 
	padding: 0 1em;
}

.wiki-syntax-list {
	margin-top: 1em;
	float: left;
}

ul.wiki-syntax-list, ul.wiki-syntax-list li ul {
	list-style: none;
	padding-left: 1em;
}

.alert-info {
	background-color: #d9edf7;
	border-color: #bce8f1;
	color: #3a87ad;
}
.alert {
	padding: 15px;
	margin-bottom: 20px;
	border: 1px solid transparent;
	border-radius: 4px;
}

code, .code {
	border-radius: 8px;
}

/* Extra sidebar */

#u-side-bar {
	display: none;
	height: 0;
	margin: 0 0 1em 1em;
}

#u-side-bar:target {
	display: block;
	height: auto;
	border: 1px solid #888888;
	z-index: 10;
}

.close {
display: none;
}

#u-side-bar:target ~ p .open {
	display: none;
}

#u-side-bar:target .close {
	display: block;
}

@media (max-width: 479px) { 
#u-side-bar {
	width: 93%;
	clear: both;
	margin: 0 auto 1em auto;
}
}

@media (min-width: 768px) and (max-width: 979px) {
#u-side-bar {
	width: 93%;
	clear: both;
	margin: 0 auto 1em auto;
}
}
[[/module]]
