[[module css]]
.extra-side-bar {
    float: right; 
    width: 15em; 
    padding: 0 1em;
}

.wiki-syntax-list {
    margin-top: 1em;
    float: left;
}

.alert-info {
    background-color: #d9edf7;
    border-color: #bce8f1;
    color: #3a87ad;
}
.alert {
    padding: 15px;
    margin-bottom: 20px;
    border: 1px solid transparent;
    border-radius: 4px;
}

code, .code {
    border-radius: 8px;
}

#content-wrap {
min-height:2100px;
}
#side-bar .default a{
display:block;
width:100%;
font-weight:110%;
padding:0.2em 0em;
}
#side-bar .menu-item {
    margin: 2px 0;
    margin-left: 1em;
}

#side-bar .menu-item li {
    margin: 0.3em 0;
}

#side-bar .menu-item li ul {
    margin-left: 1.5em;
}

#side-bar .menu-item a {
    font-weight: bold;
}

li.default{
    list-style-image: url(/local--files/nav:side/default.png);
}

li.home{
    list-style-image: url(/local--files/nav:side/home.png);
}

li.main{
    list-style-image: url(/local--files/nav:side/main.png);
}

li.series{
    list-style-image: url(/local--files/nav:side/series.png);
}

/* Mobile Media Query */
@media (max-width: 479px) { 
#side-bar ul .default{
width:100%;
}
#side-bar ul .default ul .default{
padding:0em 0em 0.1em;

}

}

/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) { 
#side-bar {
    width: 100%;
    position: relative;
}
}

@media (min-width: 768px) and (max-width: 979px) {
#header, #top-bar #side-bar {
    max-width: 100%;
}

}

.responsivescroll{
width:100%;
}


@media (max-width: 720px) {
.responsivescroll{
overflow-x:scroll;
}
.responsivescroll:before{
content:"下記の表は横スクロールが可能です。";
display:block;
}


.responsivescroll table{
width:700px;
table-layout:fixed;
word-wrap:break-word;
}


}

@media (max-width: 767px) {
#side-bar {
    display: block;
    position: fixed;
    top: 0;
    left: -25em;
    width: 20em;
    height: 100%;
    background-color: rgb(184, 134, 134);
    overflow-y: auto;
    z-index: 10;
    padding: 1em 1em 0 1em;
    -webkit-transition: left 0.5s ease-in-out 0.1s;
    -moz-transition: left 0.5s ease-in-out 0.1s;
    -ms-transition: left 0.5s ease-in-out 0.1s;
    -o-transition: left 0.5s ease-in-out 0.1s;
    transition: left 0.5s ease-in-out 0.1s;
}



#side-bar:before {
    content: "";
    background-image: url(http://scp-jp.wdfiles.com/local--files/wiki-syntax%3Aside/sidebar-logo1_50px.png);
    position: fixed;
    left: 20px;
    bottom: 20px;
    width: 40px;
    height: 40px;
    transition-duration: 0.7s;
    visibility: visible;
z-index:200;
}

#side-bar:hover {
    position: fixed;
    margin-left: 0;
 left: -25em !important;
    transition-duration: 0.7s;
    z-index:200;
}

#side-bar:hover {
    position: fixed;
    margin-left: 0;
    left: 0 !important;
    transition-duration: 0.7s;
    z-index:200;
}

#side-bar:hover:before {
    text-decoration: none !important;
    -webkit-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -moz-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -ms-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -o-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    visibility: hidden;
    border-radius: 50%;
    transition: visibility 0.5s ease 0.1s;
    transition-duration: 1s;
}

#side-bar:target + #main-content {
    left: 0;
}


#side-bar .default a{
font-size:120%;
padding:0.3em 0em;
}

#side-bar .default a:hover{
background-color: rgba(255,255,255,0.7);
}


/*blackbackground*/
#side-bar ~ #main-content:after {
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    visibility: hidden;
    background-color: rgba(0, 0, 0, 0.7);
    opacity: 0;
    content: '';
    z-index: 40;
}
#side-bar:hover ~ #main-content:after {
  visibility: visible;
  opacity: 1;
z-index:1;
  transition: opacity 500ms;
}



}

#side-bar .default a:hover{
background-color: rgba(255,255,255,0.7);
}

.open-menu{
    display:none;
}
/*Safari ハック　旧バージョン*/
@media screen and (-webkit-min-device-pixel-ratio:0) {
	::i-block-chrome, #side-bar:hover {
		position: fixed;
		margin-left: 0;
		left: -25em !important;
		transition-duration: 0.7s;
		z-index:200;
	}
    ::i-block-chrome, .open-menu{
        display:inline;
    }
    ::i-block-chrome, #side-bar:before {
        content: "";
        background-image: none;
		display:none;
    }
    ::i-block-chrome, .open-menu a{
        background-image: url(http://scp-jp.wdfiles.com/local--files/wiki-syntax%3Aside/sidebar-logo1_50px.png) !important;
        color:transparent !important;
        border:none !important;
        background-color:transparent !important;
        z-index:100;
        position: fixed;
        width:40px;
        height:40px;
    }
    ::i-block-chrome, #side-bar:hover:before{
        background-image: url(http://scp-jp.wdfiles.com/local--files/wiki-syntax%3Aside/sidebar-logo1_50px.png);
    }
	::i-block-chrome, #side-bar:target {
		position: fixed;
		margin-left: 0;
		left: 0 !important;
		transition-duration: 0.7s;
		z-index:200;
		width: 20em;
	}

	::i-block-chrome, #side-bar:target .close-menu {
		display: block;
		position: fixed;
		width: 100%;
		height: 100%;
		top: 0;
		left: 0;
		background: rgba(0,0,0,0.3) 1px 1px repeat;
		z-index: -1;
	}

	::i-block-chrome, #side-bar:hover ~ #main-content:after {
		position: fixed;
		left: 0;
		top: 0;
		width: 100%;
		height: 100%;
		visibility: hidden;
		background-color: rgba(0, 0, 0, 0.7);
		opacity: 0;
		content: '';
		z-index: 40;
	}
	::i-block-chrome, #side-bar:target ~ #main-content:after {
		visibility: visible;
		opacity: 1;
		z-index:1;
		transition: opacity 500ms;
	}
}
/* Safari ハック　新バージョン */

_::-webkit-full-page-media, _:future, :root .open-menu{
    display:inline;
}
_::-webkit-full-page-media, _:future, :root .open-menu a {
    background-image: url(http://scp-jp.wdfiles.com/local--files/wiki-syntax%3Aside/sidebar-logo1_50px.png) !important;
    color:transparent !important;
    border:none !important;
    background-color:transparent !important;
    z-index:100;
    position: fixed;
    width:40px;
    height:40px;
}

_::-webkit-full-page-media, _:future, :root #side-bar:before {
    content: "";
    background-image: none;
	display:none;
}

    _::-webkit-full-page-media, _:future, :root #side-bar:hover:before{
        background-image: url(http://scp-jp.wdfiles.com/local--files/wiki-syntax%3Aside/sidebar-logo1_50px.png);
    }

_::-webkit-full-page-media, _:future, :root #side-bar:target {
    position: fixed;
    margin-left: 0;
    left: 0 !important;
    transition-duration: 0.7s;
    z-index:200;
    width: 20em;
}

_::-webkit-full-page-media, _:future, :root #side-bar:target .close-menu {
    display: block;
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background: rgba(0,0,0,0.3) 1px 1px repeat;
    z-index: -1;
}

	_::-webkit-full-page-media, _:future, :root #side-bar:hover ~ #main-content:after {
		position: fixed;
		left: 0;
		top: 0;
		width: 100%;
		height: 100%;
		visibility: hidden;
		background-color: rgba(0, 0, 0, 0.7);
		opacity: 0;
		content: '';
		z-index: 40;
	}
	_::-webkit-full-page-media, _:future, :root #side-bar:target ~ #main-content:after {
		visibility: visible;
		opacity: 1;
		z-index:1;
		transition: opacity 500ms;
	}
/*Safariハック終わり*/

.slidTable{
border-collapse:collapse;
border-top:2px solid;
border-bottom:2px solid;
width:100%;
table-layout:fixed;
}
.slidTable td{
padding:3px 1em 3px 0;
}

.slidTable tr:nth-child(n+2){
border-top:1px solid;
}
[[/module]]
