content:
[[module css]]
#page-title{
    display:none;
}
#page-options-container {
    opacity: 0.5;
}
/* 鼠标放在上面变回不透明 */
#page-options-container:hover {
    opacity: 1;
    transition: ease-in 0.1s;
}

body{
    background:#000;
    color:#AFADA7;
    font-family: punctuation-marks, 'Huiwen-mincho', serif;
    font-size: 135%;
}

#page-title {
    font-size: 150%;
}

p {
    line-height:230%;
}

#container-warp {
    max-width:2000px;
}

a {
    color: white;
    color: white;
}

a:visited {
    color: white;
    color: white;
}

div#container-wrap {
    background: url() repeat;
}

#search-top-box,
#top-bar,
#side-bar,
#login-status,
#header  {
    display:none
}

#footer{
    opacity:0;
}
#page-content {
    min-height: 720px;
}

#main-content {
    margin:0 5% 0  5%;
}

#page-title {
    text-align: center;
    font-weight: bold;
    color: #E6E6E6;
    border-color: transparent;
}

/* ======================= */

/* MISC > EDIT BOX & PAGE SOURCE 从玄武岩抄来的*/

/* ======================= */

#lock-info {
    background: #000;
    margin-left: 1rem;
    font-size: 95%;
    border: solid 1px grey;
    color: #E6E6E6;
}

#edit-page-form>table.edit-page-bottomtable>tbody>tr>td:nth-child(1)>div.sub,
#edit-page-form>table.form>tbody>tr>td:nth-child(1) {
    font-weight: 500;
}

#edit-page-form>table.edit-page-bottomtable>tbody>tr>td:nth-child(1)>div:nth-child(1) {
    font-weight: 600;
}

.wd-editor-toolbar-panel ul li a {
    height: 22px;
    width: 22px;
    padding: 0;
    margin: 0;
    display: block;
    border-radius: 2px;
    filter: invert(1), hue-rotate(180deg);
    filter: invert(1) hue-rotate( 180deg);
}

.wd-editor-toolbar-panel {
    margin-bottom: 0.5rem;
}

input#edit-page-title.text {
    border-radius: 4px;
    border: 2px solid #afafaf;
}

textarea {
    padding: 5px 5px 5px 5px;
    font-size: 105%;
    border: 0.13rem solid #949494;
    background-color: #000;
    color: #E6E6E6;
    border-radius: 2px;
}

.page-source {
    background: #000;
    font-size: 105%;
    border: none;
    width: 80%;
    box-shadow: 0px 0px 4px #FFF BOT;
    padding: 18px 18px 18px 18px;
    word-break: break-word;
}


.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .cancel {
    background-color: transparent !important;
    border: 0px !important;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
    color: #FFFFFF;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover,
.page-rate-widget-box .cancel a:hover {
    background: transparent;
    color: #c4b2b2;
}

h1, {
    color: #E6E6E6;
    font-weight: normal;
    margin-top: 0.7em;
    padding: 0;
    font-weight: bold;
}

h1, h2, h3, h4, h5, h6 {
    color: #E6E6E6;
    font-family: punctuation-marks, 'Huiwen-mincho', serif;
}

blockquote {
    border: 0px;
    border-left: 5px rgb(175,173,167) solid;
    padding-left: 30px;
    background-color: transparent;
    margin-left: 10px;
}
#edit-page-textarea {
    font-size: 90%;
}


[[/module]]
