[[module CSS]]

:root {
    --accent: 214, 131, 0;
    --header-title: "Phoenix Technologies";
    --header-subtitle: "Breaking the Cycle";
    --logo-img: url(https://scp-wiki.wdfiles.com/local--files/wniosek-o-dotacje-marsjanski-transburzowiec/logo.png);
    --logo-opacity: 20%;

    --head-font: 'IBM Plex Sans';

    --dark-bg-1: #d68300 !important;
    --dark-bg-2: #824f00 !important;

    --link-txt-color: rgb(var(--accent));
}

body {
    background-image: linear-gradient(to bottom, #fff9f0, #fff 200px);
}

.phoenix-box {
     background: #fff9f0;
     border: double 5px rgb(var(--accent));
     margin: 1rem 0;
     padding: 0 .53rem;
}

.phoenix-box hr {
     background-color: rgb(var(--accent));
}

.al-fine {
    border: solid 4px #275495;
    background: #F2F7FB url('https://scp-wiki.wdfiles.com/local--files/theme%3Agoc/goc-fade.png') center no-repeat;
}

.research {
    display: block;
    float: left;
    width: 150px;
    margin-right: 10px;
}

@media (max-width: 575px) {
    .research {
        float: none;
        margin-left: auto;
        margin-right: auto;
        margin-bottom: 40px;
    }

    :root {
        --header-title: "Phoenix Tech";
    }
}
.stuffdiv {
    color: #f8f8f8;
    background: linear-gradient(45deg, #21252E,#2F333C) padding-box, linear-gradient(124deg, #ff2400, #e81d1d, #e8b71d, #e3e81d, #1de840, #1ddde8, #2b1de8, #dd00f3, #dd00f3) border-box;
    padding: 4px;
    box-sizing: border-box;
    border: 10px solid transparent;
}
.no-border tr td {
    border: none !important;
}
.patent {
    border: solid 5px #323232;
    padding: 10px;
    font-size: 100%;
    margin-bottom: 10px;
    font-family: "Georgia";
}

.patent tr td {
    padding: 5px !important;
    padding-left: 8px !important;
}
.nature {
    border: solid 5px #000000;
    padding: 10px;
    font-size: 100%;
    margin-bottom: 10px;
    --accent: 0, 102, 153;
    --link-txt-color: rgb(var(--accent));
}

.underline a {
    text-decoration: underline;
    text-underline-offset: 0%;
}
.unveil {
    background-color: #e6eef8;
    border: solid 3px #799dce;
    padding: 15px;
    font-size: 100%;
    margin-bottom: 10px;
    font-family: var(--mono-font);
    font-weight: bold;
    --accent: 61, 139, 44;
    --link-txt-color: rgb(var(--accent));
}

.unveil hr {
    background-color: #799dce;
}
