[[module CSS]]
#header h1 a::before {
    color: transparent;
}

#header h2 span::before {
    color: transparent;
}

#header {
    background-image: url( );
    background-size: 6rem;
}

#page-title {
    text-align: center;
    border-color: #bbb;
}

div#container-wrap {
background: url(http://smlt.wikidot.com/local--files/readeiier:jp/logo.png) top center repeat-x;}

#login-status {
    color: white;
    filter: grayscale(100%) saturate(0%);
    -webkit-filter: grayscale(100%) saturate(0%);
}

#login-status a {
    color: white;
}

#login-status ul a {
    color: white;
}

#account-topbutton {
     background: #ccc;
    color: white;
}
[[/module]]

