[[module CSS]]
 :root {
	--lgurl: url(https://scp-wiki.wdfiles.com/local--files/component:pride-highlighter/lgbtqp_logo.svg);
}

 .forum-mini-stat a {
	font-weight: bold;
}

 h1 {
	margin-top: 3rem;
}

 table.wiki-content-table {
	display: block;
	max-height: 85vh;
	overflow: auto;
}

 .special table {
	counter-reset: line-number;
}

 .special table a {
	font-weight: bold;
}

 .special td:first-child:before {
	content: counter(line-number) ".";
	counter-increment: line-number;
	padding-right: .3rem;
}

 .special td:nth-child(3) {
	min-width: 25%;
}

 .special td:nth-child(4) {
	min-width: 20%;

	font: 1rem monospace;
	text-align: right;
}

 #page-content {
	margin-bottom: 20rem;
}
[[/module]]

